INSERT INTO `clothes` (`Clothes_id`, `Clothes_name`, `Clothes_detail`, `Ethnic_id`) VALUES
('CL0001', 'ชุดเชวา', 'เป็นการแต่งกายของผู้หญิงกะเหรี่ยงทุกวัยที่ยังโสด จะแต่งด้วยผ้าทรงกระสอบสีขาว มีลวดลายแต่งตามตัวเล็กน้อย บริเวณหน้าอกมีลายแดงทำเป็นชายครุย เมื่อแต่งงานแล้วต้องเปลี่ยนเป็นชุดเชโมซู', 'E06'),
('CL0002', 'ชุดเชโมซู', 'เป็นการแต่งกายของผู้หญิงกระเหรี่ยงที่แต่งงานแล้ว จะใส่ผ้าถุงสีแดงมีลายขวางตามตัว ซิ่นเป็นเส้นสีดำ ฟ้า น้ำตาล ส่วนเสื้อท่อนบนจะแต่งตามเผ่า โดยเมื่อแต่งงานแล้วห้ามกลับไปใส่ชุดเชวาอีก', 'E06'),
('CL0003', 'ชุดการแต่งกายผู้ชายกะเหรี่ยง', 'ลักษณะเสื้อผู้ชายวัยหนุ่มใช้สีแดงทุกกลุ่ม แต่มีลวดลายมากน้อยต่างกัน และสวมโสร่ง หรือกางเกงสีดำ น้ำเงินหรือกรมท่า การแต่งกายในโอกาสพิเศษ เช่น พิธีปีใหม่ พิธีงานแต่งจะเน้นสวมเสื้อผ้าใหม่', 'E06'),
('CL0004', 'หญาดฮะเหริ่มโตะ ', 'หรือผ้าสไบ มีลักษณะที่โดดเด่นคือ การปักลายตลอดริมผ้า ซึ่งลายนี้จะแสดงถึงกลุ่มของตนได้ ผ้าสไบของมอญจะไม่กว้างเหมือนสไบของไทย คือ กว้างประมาณ 12-15 ซม. และยาวประมาณ 110-200 ซม. เพราะคนมอญจัมีวิธีใช้สไบแบบคล้องคอและปล่อยชายลงมาทั้งสองข้าง จึงไม่นิยมขนาดกว้างเกินไปเพราะจะทำให้เกะกะ ปกตินิยมพาดสไบสีที่เข้ากับผ้านุ่งของตนเอง ', 'E53'),
('CL0005', 'เกลิดฮะเหลิ่น', 'ผู้ชายชาวมอญจะสวม เกลิด ซึ่งก็คือผ้าผืนยาวที่นุ่งเวลาออกงานสำคัญ ', 'E53'),
('CL0006', 'หนิ่น', 'ผู้หญิงจะสวม หนิ่น ซึ่งจะคล้ายผ้านุ่งของผู้ชาย แต่ลายของผู้หญิงละเอียด สวยงามกว่า และวิธีการนุ่งต่างกัน ', 'E53'),
('CL0007', 'ฉีผาว (ชุดกี่เพ้า)', 'ฉีผาวหรือกี่เพ้าเป็นชุดที่ดัดแปลงมาจากชุดของหญิงชาวแปดกองธงในสมัยราชวงศ์ชิง กี่เพ้าเป็นเครื่องแต่งกายที่เกิดจากการหลอมรวมเป็นหนึ่งของชนชาติต่างๆ ของจีนและถือเป็นมรดกทางวัฒนธรรมการแต่งกายของชาวจีน กี่เพ้าเป็นงานตัดเย็บที่รวมเอาศิลปะหลายแขนงไว้ด้วยกัน ไม่ว่าจะเป็นหัตถกรรมการปักลวดลาย ภาพดอกไม้และนกหรือภาพอื่นๆ ซึ่งล้วนสะท้อนให้เห็นถึงเอกลักษณ์ทางวัฒนธรรมของจีน ', 'E15'),
('CL0008', 'ถังจวง (เสื้อคอจีน)', 'ถังจวงหรือเสื้อคอจีนดัดแปลงมาจากเสื้อนอกของชายในสมัยปลายราชวงศ์ชิง แบบเสื้อถังจวงมีลักษณะเด่น 4 ประการคือ 1.คอเสื้อตั้ง โดยเปิดคอเสื้อด้านหน้าตรงกลางไว้ 2.แขนเสื้อและตัวเสื้อเป็นผ้าชิ้นเดียวกัน จึงไม่มีรอยตะเข็บต่อระหว่างแขนเสื้อและตัวเสื้อ 3. สาบเสื้อเป็นแนวตรงหรือแนวเฉียง 4. กระดุมเสื้อเป็นกระดุมแบบจีนซึ่งประกอบด้วยเม็ดกระดุมที่ใช้ผ้าถักเป็นปมและห่วงรังดุม     ', 'E15'),
('CL0009', 'การแต่งกายผู้หญิงอิ้วเมี่ยน', 'เสื้อผ้าที่มีการปักลวดลายอย่างประณีต งดงาม อีกทั้งยังสวมใส่เครื่องประดับเงินนานาชนิด โดยเฉพาะในช่วงงานพิธีกรรมหรือประเพณีสำคัญ  ผู้หญิงอิ้วเมี่ยนมักจะนิยมสวมเครื่องประดับเงินต่าง ๆ ทั้งแหวน กำไลมือ สร้อยคอ จี้ และยังมีเครื่องประดับเงินที่ติดกับสร้อยคอ รวมทั้งกระดุมเงินที่ประดับกันอย่างมากมายอยู่บนเสื้อปักลายและผ้าที่ประดับด้วยผ้าตัดปะ', 'E72'),
('CL0010', 'การแต่งกายผู้ชายอิ้วเมี่ยน', 'เสื้อตัวสั้นหลวม คอกลม  ชิ้นหน้าห่ออกอ้อมไปติดกระดุมลูกตุ้มเงินแปดถึงสิบเม็ดเป็นแถวทางด้านขวา  กางเกงจะเป็นกางเกงขาก๊วยไม่มีการปักลาย ทั้งเสื้อและกางเกงจะตัดเย็บด้วยผ้าทอมือย้อมครามสีน้ำเงินหรือสีดำ', 'E72'),
('CL0011', 'ชุดกะเหรี่ยงเผ่าสะกอ(หญิง)', 'สวมกระโปรงยาวถึงข้อเท้า ผมเกล้ามวยไว้ข้างหลัง พันด้วยเส้นด้ายถักสีแดง ผ้าส่วนมากจะทอเอง เสื้อแขนกุดพื้น ดำ หรือน้ำเงินปนดำ ส่วนล่างของเสื้อปักลวดลายทรง เรขาคณิตด้วยด้ายแดง ปักลูกเดือย มีพู่ชายครุยบริเวณชายเสื้อ', 'E67'),
('CL0012', 'ชุดกะเหรี่ยงเผ่าสะกอ(ชาย)', 'สวมกางเกงขายาวแบบจีน สีดำ หรือขาว สวมเสื้อสีแดงยกดอกเป็นหมู่ แขนสั้น เสื้อยาวครึ่งเข่า บางคนสวมเสื้อเชิ้ต สีขาวด้วยเสื้อชุดสีแดง บางคนสวมชุดดำ', 'E67'),
('CL0013', 'ชุดกะเหรี่ยงแดง(หญิง)', 'สวมชุดสีดำ และแดง มีผ้าขาวม้าพันเอว มีชายห้อยลงมาด้านหน้า 2 แฉก มีผ้าผืน ใหญ่คลุมไหล่ ผม', 'E08'),
('CL0014', 'ชุดกะเหรี่ยงแดง(ชาย)', 'สวมเสื้อสีแดง สีดำ นิยมสวมเสื้อกางเกงลายสลับขาวเป็นเส้นตรงลงมา', 'E08'),
('CL0015', 'ชุดกะเหรี่ยงโพล่ง(หญิง)', 'หญิงสวมเสื้อ แขนกุดพื้น แดงอ่อนหรือสีน้ำหมาก ตัวเสื้อครึ่งล่างปักลวดลายเป็น เส้นตรงแนวนอนเป็นช่วง ๆ เกล้ามวยผมสูงเป็นกระพุ่มกลมแผ่บาน ขมวดเป็นจุก ส่วนการแต่งกายโดยทั่วไปจะคล้าย ๆ กัน', 'E09'),
('CL0016', 'ชุดกะเหรี่ยงโพล่ง(ชาย)', 'แขนกุด ชายต่ำกว่าเอว สีและลวดลายเหมือนเผ่าสะกอ กางเกงเป้าต่ำสีน้ำเงินเข้ม หรือดำ โพกหัวด้วยผ้าสีแดง หมวกขาวสีน้ำตาล', 'E09'),
('CL0017', 'ชุดเชวา', 'เป็นเครื่องแสดงถึง “สาวพรหมจารี” หรือ หญิงบริสุทธิ์ มีลักษณะเป็นชุดยาวสีขาว โดยจะมีลายทอตรงส่วนเอวต่างสีต่างลายกันตามแต่ละบ้านขึ้นอยู่กับสิ่งที่ถูกสืบทอดต่อกันมา โดยมาก ชุดเชวาไม่ได้ถูกทอเพื่อการสร้างรายได้ แต่แม่หรือคนในบ้านจะเป็นคนทอให้บุตรหลานผู้หญิงในบ้านใส่ ', 'E41'),
('CL0018', 'ชุดม้ง(หญิง)', 'เสื้อแขนยาวสวมทับด้วยกระโปรง สาบเสื้อสองข้างจะปักด้วยลวดลายหรือขลิบด้วยผ้าสีกระโปรงจีบรอบปักเป็นลวดลายต่างๆ มีผ้าสี่เหลี่ยมผืนยาว\nปักลวดลายปิดด้านของกระโปรง มีผ้าสีแดงคาดเอวทับอีกทีหนึ่งโดยการผูกปล่อยชายเป็นหางไว้ด้านหลัง ใช้ผ้าสีดําที่ประดับด้วย\nลูกปัดสีต่างๆพันมวยผม ใส่สายสะพายไหล่ หรือย่าม เฉียงสลับกันสองข้าง', 'E48'),
('CL0019', 'ชุดม้ง(ชาย)', 'สวมเสื้อแขนยาวถึงข้อมือ ชายเสื้ออยู่ระดับเอว\nปกเสื้อด้านขวาจะป้ายเลยมาทับซีกซ้ายของตัวเสื้อ ตรงสาบเสื้อจะใช้ด้ายสีปักลวดลายต่างๆกางเกงสีเดียวกับเสื้อมีลักษณะขา\nกว้างตรงปลายขาแคบ เป้ากางเกงจะหย่อนลงมาต่ํากว่าระดับเข่าปลายขากางเกงปักลวดลาย', 'E48'),
('CL0020', 'ชุดอ่าข่า(หญิง)', 'สวมหมวกโลหะสีเงิน ประดับด้วยขนสัตว์ เช่น ขนม้า ขนไก่ ด้านบนหมวกคลุมด้วยผ้าปัก สีสันลวดลายสดใสสี่เหลี่ยมผืนน้อย ที่เรียกว่า อูเบว', 'E70'),
('CL0021', 'ชุดอ่าข่า(ชาย)', 'สวมเสื้อแขนยาวเสื้อกั๊กสีดำ ปักลวดลายเล็กน้อยตามขอบชายเสื้อ สวมกางเกงสีดำ สะพายย่าม', 'E70'),
('CL0022', 'ชุดลาหู่(หญิง)', 'สวมใส่เสื้อแขนยาว สวมผ้าถุงยาวถึงข้อเท้า ตกแต่งด้วยแถบผ้าสีต่างๆและ มีเม็ดอลูมิเนียมเล็กๆ เย็บติดเสื้อ และมีสวดลายต่างๆ แปะติดไว้ด้านหน้าและด้านหลังอย่างสวยงาม', 'E63'),
('CL0023', 'ชุดลาหู่(ชาย)', 'สวมใส่เสื้อแขนยาวสีดำ ประดับด้วยเม็ดโลหะเงินและลายปักต่างๆ ส่วนกางเกงใช้สีดำ สีเขียว สีฟ้า เย็บปักด้วยมือที่สวยงาม', 'E63'),
('CL0024', 'ชุดโอก๋อง(ชาย)', 'ผู้ชายจะแต่งกายด้วยเสื้อผ้าสีดำ นุ่งกางเกงขาก๊วยยาวคลุมเข่าเล็กน้อย สวมเสื้อแขนยาว มีปกตั้งคล้ายเสื้อคอจีน ผ่าหน้ามีกระดุมเรียงต่อกันลงมา สาบเสื้อทับกัน บริเวณปกเสื้อและสาบเสื้อจะมีการปักลวดลายด้วยไหมพรมสีสันสวยงาม ส่วนศีรษะโพกผ้าผืนยาว เพื่อให้ความอบอุ่น นิยมสะพายย่ามสีแดงสลับขาวเวลาออกจากบ้าน', 'E74'),
('CL0025', 'ชุดโอก๋อง(หญิง)', 'นุ่งผ้าซิ่น เหน็บชายผ้าไว้ที่เอว คาดเข็มขัดทับหรือใช้เชือกมัด ลักษณะของผ้านุ่งไม่มีหัวซิ่นหรือตีนซิ่น สีที่นิยมในสมัยก่อนคือสีเขียวแก่ สีแดงคล้ำ และสีน้ำเงิน สวมเสื้อสีดำ แขนสั้น ปกตั้งคล้ายของผู้ชาย ด้านหน้าผ่าตลอด สาบเสื้อชนกันอาจจะใช้กระดุมติดหรือใช้เชือกผูก ที่ด้านนอกของบริเวณรอยต่อของแขนเสื้อกับตัวเสื้อนิยมเย็บด้วยด้ายสีแดงเป็นลายกากบาท ส่วนศีรษะโพกผ้าเช่นกันแต่มีรูปแบบต่างจากผู้ชาย ผู้หญิงนิยมใช้ถุงย่ามสีขาว', 'E74'),
('CL0026', 'ชุดก่อ(อึมปี้)(หญิง)', 'จะแต่งกายด้วยผ้าซิ่นสีดำ มีแถบสีแดงสองแถบบริเวณเชิงซิ่นและชายหัวซิ่นหรือซิ่นตามสมัยนิยม สวมเสื้อคอกลมผ่าหน้าที่สาบเสื้อด้านหน้าประดับไปด้วยกระดุมเงิน แขนเสื้อรูปทรงกระบอก นิยมนำผ้าสไบสีขาวคล้องคอ รวมถึงสวมกำไลเงินแบบเกลียว', 'E01'),
('CL0027', 'ชุดก่อ(อึมปี้)(ชาย)', 'สวมเสื้อคอกลมผ่าหน้าหรือเสื้อหม้อฮ่อมตามแบบคนเมืองแพร่', 'E01'),
('CL0028', 'ชุดกะเหรี่ยงคอยาว(หญิง)', 'บริเวณรอบคอจะสวมใส่ห่วงทองเหลืองที่มีการขดม้วนเรียงกันหลายชั้น ตั้งแต่ไหปลาร้าจรดคาง ทรงผมด้านหน้าจะไว้หน้าม้า ด้านหลังจะมัดเป็นมวยแล้วใช้ผ้าสีเขียว สีชมพูคาดทับทิ้งชายห้อยระบ่า แขนจะใส่กำไลที่ทำจากอะลูมิเนียมข้างละ 3-5 วง และที่ขาบริเวณใต้หัวเข่าจะสวมห่วงทองเหลืองไว้อีกข้างละประมาณ 10-15 วง รองด้วยผ้าสีชมพู และจากน่องลงมาถึงข้อเท้าจะพันด้ายผ้าสีน้ำเงิน เสื้อที่สวมใส่เป็นสีขาวคอวีทรงกระสอบ ตัวยาวถึงสะโพกล่าง ผ้าถุงสีกรมท่าสั้นแค่หัวเข่า มีลวดลายเป็นเส้นสีชมพูรอบ ชายผ้าถุงแคบและนุ่งพับทบกันด้านหน้า', 'E07'),
('CL0029', 'ชุดกะเหรี่ยงคอยาว(ชาย)', 'นุ่งกางเกงขาก๊วยแบบจีน เสื้อที่สวมใส่เป็นสีขาวคอวีทรงกระสอบ ตัวยาวถึงสะโพก ด้านหลังสีขาว ส่วนด้านหน้าท่อนบนจะเป็นสีขาว ตั้งแต่อกลงมาจะเป็นสีแดง ศีรษะโพกผ้าสีขาว ถ้ามีกิจกรรมหรืองานประเพณีจะสวมกำไลข้อเท้าที่ทำจากลูกปัดสีขาว น่องตอนบนจะใส่กำไลไม้ไผ่หรือหวาย', 'E07'),
('CL0030', 'ชุดชาวกะเลิง', 'ผ้าซิ่น ใช้ด้าย 2 เส้นมาทำเกลียวควบกัน ใช้ทั้งผ้าฝ้ายธรรมดาและผ้าไหม เป็นผ้าตีนเต๊าะ แต่มีเชิงแถบเล็กๆ แคบ 2 นิ้ว นิยมสีเปลีอกอ้อย เข็นด้วยด้ายสีแดง เหลืองเป็นสายเล็กๆ นอกจากนี้ยังนิยมใช้ผ้าฝ้ายเข็น 2 เส้นควบกัน เช่น แดงควบเหลือง น้ำเงินควบขาว เขียวควบเหลือง ถ้าไม่ใช่เป็นผ้าตีนเต๊าะมักนุ่งสั้น\nเสื้อ กะเลิงนิยมแต่งตัวกะทัดรัด เช่น ถ้านุ่งซิ่นผ้าฝ้ายสั้น มักใช้ผ้าทอพื้นบ้าน เป็นตาสี่เหลี่ยมเล็กๆ คาดอก โพกผ้าบนศีรษะ สะพายกะหยัง ขึ้นภูเก็บผักเก็บหญ้าส่วนกะเลิง ที่นุ่งซิ่นยาวคลุมเข่ามักสวนเครื่องประดับ เช่น สร้อยข้อมือทำด้วยรัตนชาติ หรือดินเผา ใส่ต่างหูเป็นห่วงกลม เสื้อแขนยาวสีขาวเหลืองเก็บชายเสื้อคาดเข็มขัดเงิน เป็นชุดที่ใช้ในงานมงคลต่างๆ นับเป็นเครื่องแต่งกายที่งดงามที่สุดของชาวเผ่ากะเลิงวัยหนุ่มสาว ส่วนกะเลิงสูงอายุ มักนุ่งซิ่นลายดำ ขาว แดง สวมเสื้อแขนกระบอกย้อมคราม ที่สาบเสื้อมีเหรียญสตางค์แดงติดเป็นแนวกระดุม เกล้าผมสูง', 'E04'),
('CL0031', 'ชุดชาวกูย', 'หญิงสูงอายุจะนุ่งผ้าที่มีลายใส่เสื้อคอกระเช้า ใส่สร้อยคอลูกปัดเงิน นิยมใส่ดอกไม้หอมไว้ที่ติ่งหู ชาวกูยนิยมทอผ้า เช่น ผ้าจิกกะน้อย เป็นผ้าที่มีลักษณะคล้ายผ้าหางกระรอกมีสีเดียวเป็นผ้าสำหรับผู้ชายนุ่งในพิธีสำคัญๆ ลักษณะการนุ่งจะนุ่งพับจีบด้านหน้า เหมือนการนุ่งโสร่ง ผ้านุ่งสตรีนิยมทอหมี่คั่นเป็นทางแนวดิ่งยืนพื้นสีน้ำตาล มีหัวซิ่นพื้นสีแดงลายขิด ตีนซิ่นสีดำมีริ้วขาวเหลืองแดงผ้าจะกวีเป็นผ้าคล้ายอันลูซีม ของเขมรมีลายทางยาวเป็น ผ้าที่สตรีใช้นุ่งในงานสำคัญๆ​', 'E10'),
('CL0032', 'ชุดชาวขมุ', 'จะใช้ซิ่นลายขวางแบบไทลื้อ สวมเสื้อผ้าหนาสีน้ำเงินเข้มตัวสั้น ตกแต่งด้วยผ้าด้ายสีและเหรียญเงิน ใส่กำไลเงินที่คอ และกำไลข้อมือ โพกผ้าสีขาว หรือสีแดง ', 'E11'),
('CL0033', 'ชุดขแมร์ลือ(หญิง)', 'นุ่งผ้าถุง ซึ่งเป็นผ้าไหมพื้นเมืองที่มีชื่อเสียง คือ ผ้าโฮล ผ้าอำปรุม ผ้าอัลลุยซีม ผ้าซาคู ใส่เสื้อแขนกระบอก เครื่องประดับจะเป็นเครื่องเงิน เรียกว่า ประเกือม (ประคำ) นำมาร้อยเป็นสร้อย ต่างหู เป็นต้น', 'E12'),
('CL0034', 'ชุดขแมร์ลือ(ชาย)', 'นุ่งโสร่ง ใส่เสื้อคอกลม', 'E12'),
('CL0035', 'ชุดไทยวน(หญิง)', 'การแต่งกายของหญิงไทยวนจะมีความโดดเด่นเป็นเอกลักษณ์ของตนเอง คือ “ผ้าซิ่น” ที่มีลักษณะเป็นผ้าริ้วลายขวาง ต่อตีนด้วยผ้าสีแดงหรือดำและต่อหัวซิ่นด้วยผ้าสีขาว สีแดง หรือดำ หรืออาจจะเป็นผ้าสีเดียวก็ได้ โดยการเย็บเข้าด้วยกันเรียกซิ่นชนิดนี้ว่า “ซิ่นต๋า” หรือ “ซิ่นต่อตีนต่อเอว”', 'E33'),
('CL0036', 'ชุดไทยวน(ชาย)', 'นุ่งกางเกงชักเปาลักษณะคล้ายกางเกงจีนย้อมด้วยสีคราม สวมเสื้อแลบแดง หน้าอกประดับด้วยลูกกระดุมเงิน คาดเอวด้วยกระเป๋าถักสำหรับใส่สัมภาระต่างๆ', 'E33'),
('CL0037', 'ชุดผู้ไท ภูไท(หญิง)', 'นิยมนุ่งผ้าซิ่นที่ทำจากผ้า ซึ่งลักษณะเด่นของซิ่นภูไท คือ การทอและลวดลายเช่น ทอเป็นลายนาคเล็กๆ นอกจากนี้มีลายอื่นๆ เช่น หมี่ปลา หมี่กระจัง หมี่ข้อ หมี่ขอ ทำเป็นหมี่คั่นหรือหมี่ลวด ต่อด้วยหัวซิ่นและตีนซิ่นทั้งขิดและจก นอกจากนี้ยังพบผ้ามัดหมี่ฝ้ายสีขาวสลับดำย้อมใบครามหรือมะเกลือสีดำ เย็บต่อด้วยหัวซิ่นตีนซิ่น', 'E45'),
('CL0038', 'ชุดผู้ไท ภูไท(ชาย)', 'นิยมนุ่งกางเกงขาก๊วย สีดำหรือนุ่งโสร่งตาหมากรุก เสื้อใช้ผ้าสีครามหรือดำชนิดเดียวกับกางเกง สวมเสื้อคอกลมแคบชิดคอหรือคอจีน ตัวเสื้อผ่าอกตลอด ชายเสื้อผ่าข้าง จะเป็นแขนยาวหรือแขนสั้นก็ได้ มีผ้าคาดเอว และโพกศีรษะ ผู้ชายโบราณมักนิยมสักแขนขา ลายด้วย หมึกสีดำ แดง ถือเป็นเครื่องรางและแสดงออกถึงความเป็นชายชาตรี', 'E45'),
('CL0039', 'ชุดจีนยูนนาน(หญิง)', 'ไว้ผมมวย แต่งกายคล้ายชาย แต่ผ่าอกป้ายมาทางไหล่ข้างซ้าย ติดกระดุมผ้าใต้รักแร้ ใช้ผ้าสีขาวสับด้วยแถบชายผ้าใหญ่สีดำ', 'E15'),
('CL0040', 'ชุดจีนยูนนาน(ชาย)', 'สวมหมวกกลม ๆ ไม่มีปีก แต่มียอดจุกอยู่ตรงกลางเรียกว่า “ กะน้าก้วย ” เสื้อหลายชั้นชิดต้นคอ ผ่าอกป้ายข้าง หรือผ่าอกกลาง เสื้อยาวใต้เอวเล็กน้อย แขนเสื้อกว้างยาวหุ้มข้อมือ นิยมใช้สีดำมากกว่าสีขาว ใช้กระดุมผ้า กางเกงขากว้าง', 'E15'),
('CL0041', 'ชุดชาวโซ่(ทะวืง)(หญิง)', 'นุ่งผ้าถุง ใช้ผ้าขาวม้าปกปิดร่างกายส่วนบน ใส่ต่างหู (กระจอนหู) ทำมาจากเงิน', 'E18'),
('CL0042', 'ชุดชาวโซ่(ทะวืง)(ชาย)', 'นิยมนุ่งผ้าขาวม้า ย้อมผ้ามาจากสีธรรมชาติและทอผ้าใช้เอง', 'E18'),
('CL0043', 'การแต่งกายชุดรำไทยญ้อ(หญิง)', 'สวมเสื้อแขนกระบอกสีชมพู (สีบานเย็น) คอกลมขลิบดำ หรือน้ำเงินเข้ม นุ่งผ้าถุงไหมสีน้ำเงินมีเชิง  (ตีนจก)  เข็มขัดลายชิดคาดเอว ใช้สไบไหมสีน้ำเงินพาดไหล่ด้านซ้ายแบบเฉียงปล่อยชายยาวทั้งด้านหน้าและด้านหลังให้ชายเท่ากัน เครื่องประดับสร้อยคอ ตุ้มหู สร้อยข้อมือเครื่องเงิน ผมเกล้ามวยประดับดอกไม้สด หรือดอกไม้ประดิษฐ์ ', 'E19'),
('CL0044', 'การแต่งกายชุดรำไทยญ้อ(ชาย)', 'สวมเสื้อคอพวงมาลัยสีเขียวสด  ใช้สไบไหมสีน้ำเงินพับครึ่งกลาง พาดไหล่  ซ้ายและขวา  ปล่อยชายสองข้างไปด้านหลังให้ชายเท่ากัน นุ่งผ้าโจงกระเบนสีน้ำเงินเข้ม ใช้สไบไหมสีแดงคาดเอว ปล่อยชายข้างซ้ายด้านหน้า เครื่องประดับสร้อยเงิน ห้อยพระ  ใบหูทัดดอก ดาวเรืองด้านซ้าย', 'E19'),
('CL0045', 'เสื้อพ็อก', 'เครื่องแต่งกายพื้นเมืองของชาวญัฮกุร ที่ใส่ในชีวิตประจำวัน และใช้ใส่ในงานพิธีต่างๆ ลวดลายบนผืนผ้าจะเป็นลายที่เกี่ยวกับชีวิตประจำวัน เอามาดัดแปลงเป็นลายผ้าได้อย่างลงตัว ปักเย็บด้วยความประณีต ปักด้วยมือทุกผืน', 'E20'),
('CL0046', 'ชุดชาวเญอ(หญิง)', 'เสื้อ ทอจากผ้าไหมลายลูกแก้วยกดอกย้อมมะเกลือไม่อัดกาว ทรงตรง แขนกระบอก เป็นคอกลมธรรมดา มีกระเป๋าเสื้อ ในอดีตเสื้อผู้หญิงนิยมประดับด้วยกระดุมเม็ดเงินแท้เย็บต่อกับผ้าด้วยด้ายสีแดง\nสไบ/ผ้าขาวม้า ทอจากผ้าไหมลายลูกแก้วสีดำย้อมมะเกลือ มีการถักชายผ้าสไบด้วยด้ายหลากสีสันสดใส\nผ้าซิ่น/สโร่ง/กางเกง ทอจากผ้าไหมพันธุ์พื้นบ้าน ส่วนใหญ่ย้อมเป็นสีเขียว เขียวแดง ม่วง ทอเป็นลายหางกระรอก ผ้าซิ่น 1 ผืน ต้องมีส่วนประกอบมาต่อกันทั้งหมด 5 ชิ้น ได้แก่ หัวซิ่น 2 ชิ้น ตัวผืน 1 ชิ้น และตีนซิ่น 2 ชิ้น โดยหัวซิ่นและตีนซิ่นจะมีลักษณะคล้ายกัน คือ มี 2 ชิ้น เย็บติดกัน ได้แก่ ชิ้นกว้าง 10 เซนติเมตร และกว้าง 2 เซนติเมตร (เรียกว่าแถบ) โดยชิ้นกว้าง 10 เซนติเมตร นิยมทอด้วยลายขอ และเย็บต่อด้วยเส้นแถบ', 'E21'),
('CL0047', 'ชุดชาวเญอ(ชาย)', 'เสื้อ เป็นผ้าไหมลายลูกแก้วสีดำย้อมมะเกลือ แขนยาว คอกลม ทรงตรง ประดับด้วยกระดุมเม็ดเงินแท้\nสไบ/ผ้าขาวม้า ในอดีตผ้าขาวม้าจะทอจากผ้าไหมพันธุ์พื้นบ้าน ไม่ยกดอก แต่ปัจจุบันนิยมใช้ผ้าฝ้ายมาทอเป็นผ้าขาวม้า ผ้าขาวม้าที่ทอขึ้นใช้งานส่วนใหญ่เป็นลายตารางเช่นเดียวกับผ้าโสร่ง แต่ตารางจะมีขนาดเล็กกว่าผ้าโสร่ง มีหลายสีเช่นกัน\nผ้าซิ่น/สโร่ง/กางเกง ทอจากผ้าไหมพันธุ์พื้นบ้าน ทอแบบธรรมดาไม่ยกดอก ทอเป็นลายตารางขนาดใหญ่คั่นตารางแต่ละแถวด้วยการใช้สีที่แตกต่างกัน ดังนั้นในระหว่างทอจึงต้องใช้ไหมหลายสีในหนึ่งผืน สีที่นิยมทอเป็นผ้าโสร่ง ได้แก่ สีเขียวอมเหลือง แดง น้ำตาล เป็นต้น ผ้าโสร่งสำหรับผู้ชายจะไม่มีหัวและตีนผ้า เนื่องจากเน้นความสะดวกในการใช้งาน', 'E21'),
('CL0048', 'ชุดชาวดาราอาง(หญิง)', 'สวมเสื้อสีดำ น้ำเงิน หรือเขียว เอวลอย ผ่าหน้า แขนกระบอก ตกแต่งด้วยแถบผ้าสีแดงประดับเสื้อผ้าด้วยลูกปัดหลากสี ผ้าซิ่นสีแดงสลับลายริ้วขาวเล็กๆ ขวาง ลำตัว ยาวกรอมเท้า โพกศีรษะด้วยผ้าผืนยาว ปัจจุบันนิยมใช้ผ้าขนหนู ลักษณะที่โดดเด่น คือการสวมเอวด้วยวงหวาย บางคนก็ใช้โลหะสีเงินตัดเป็นแถบยาวตอกลาย แล้วขดเป็นวง สวมใส่ปนกัน เรียกว่า \"หน่องว่อง\" ปัจจุบันพบเพียงนุ่งกางเกงสะดอสีน้ำเงิน สวมเสื้อเชิ้ตสีขาว หรือสีสดใส โพกศรีษะด้วยผ้าขาว ลักษณะที่โดดเด่น ได้แก่การแกะฟัน และฝังด้วยทองหรือพลอยสีต่าง', 'E22'),
('CL0049', 'ชุดชาวดาราอาง(ชาย)', 'เครื่องแต่งกายของชายชาวดาระอั้ง ค่อนข้างที่จะเรียบง่ายกว่าเครื่องแต่งกายของผู้หญิง เสื้อผู้ชายจะเป็นเสื้อแขนยาว ผ่าหน้าคล้ายกับเสื้อไทยใหญ่ ทำจากผ้าฝ้ายสีน้ำเงินหรือสีดำ ความยาวพอดีกับเอว ผ้าโพกศีรษะของผู้ชายจะเริ่มโพกโดยใช้ชายผ้าด้านหนึ่งวางไว้บนศีรษะแล้วขมวดผ้าส่วนที่เหลือจนรอบ โดยทั่วไปแล้วจะพบ ผู้ชายสวมใส่เสื้อดาระอั้งน้อยมาก ส่วนใหญ่จะมีเฉพาะผู้เฒ่าผู้แก่ อาจจะเป็นเพราะวิถีชีวิตที่เปลี่ยนไป ต้องทำงานกลางแจ้งจึงไม่สะดวกที่จะสวมเสื้อดาระอั้ง ชายหนุ่มดาระอั้งจึงนิยมที่จะสวมเสื้อเชิ้ตมากกว่า โดยในเวลาที่ต้องทำงานจะสวมเสื้อเชิ้ตสีเข้ม แต่หากมีงานรื่นเริงจะสวมเสื้อเชิ้ตหลากสีแทน', 'E22'),
('CL0050', 'กางเกงเซียม', 'ทำจากผ้าฝ้ายสีดำหรือน้ำเงิน ลักษณะคล้ายกับกางเกงเล หรือ กางเกงไทยใหญ่ โดยเวลาสวมใส่จะทบให้กระชับกับลำตัว แล้วใช้เชือกหรือเข็มขัดรัดให้แน่น ผู้ชายดาระอั้งจะสวมกางเกงเซียม ทั้งในชีวิตประจำวันและเมื่อมีงานฉลองต่างๆ ถ้าเป็นเด็กวัยรุ่นชายจะชอบรัดเข็มขัดโดยปล่อยชายเข็มขัดให้ห้อยลงมาเป็นแฟชั่นฮิตมากในชุมชน', 'E22'),
('CL0051', 'ชุดไต(หญิง)', '“เส้อแซค” หรือ “เส้อไตหน้าแว๊ด” แขนเสื้อเป็นผ้าชิ้นเดียวกับตัวเสื้อ ชายเสื้อสั้นเท่าเอวติดกระดุมขอดห้าคู่\nเสื้อไตต่อเอ็นหลัง หน้าตรง แบบคอกลม ผ่าหน้า\nเสื้อไตหน้าแว๊ดไม่ต่อแขนหรือแขนเลย มีทั้งแบบต่อเอ็นหลัง ต่อเอ็นหน้า ต่อเอ็นหลัง ไม่ต่อเอ็นหน้า\nเสื้อไตหน้าแว๊ดต่อไหล่ ต่อเอ็นหน้าหรือไม่ก็ได้ ไม่ต่อเอ็นหลัง\nเสื้อไตแบบผู้ชาย มีทั้งแขนสั้น แขนยาว และ\nผ้าซิ่นหรือผ้าถุงจะใช้ผ้าที่มีลวดลายเป็นส่วนใหญ่ เย็บตะเข็บเดียวเป็นผ้าถุง', 'E23'),
('CL0052', 'ชุดไต(ชาย)', 'กางเกงขาก๊วย เรียกว่า ก๋นห่งย่ง หรือ ก๋นไต \nเสื้อแขนยาว คอกลม ไหล่เลยลงมาต่อตะเข็บตรงกึ่งกลางแขน \nเสื้อผ่าหน้าติดด้วยกระดุมขอดห้าคู่ ตัวยาวเท่าสะโพก เรียกว่า เสื้อแต้กปุ่ง ', 'E23'),
('CL0053', 'ผ้าไต่เซิน', 'เป็นผ้าพื้นสีดำ ประดับด้วยริ้วผ้าสีต่างๆเย็บเป็นแถบชายซิ่น', 'E24'),
('CL0054', 'ผ้าเซิน', 'เป็นผ้าพื้นสีดำประดับชายผ้าด้วยริ้วผ้าสีแต่ไม่เย็บด้านข้างให้ติดกัน ใช้สวมทับผืนแรก(ผ้าไต่เซิน)โดยพันรอบตัวให้ชายผ้าขนานผืนแล้วคาดเข็มขัดทับ', 'E24'),
('CL0055', 'ซื่อแย่ง', 'เสื้อไว้สวมตัวที่ 2 เป็นเสื้อไม่มีแขน คอปิด ไม่มีปกผ่าหน้าเฉียงมาทางซ้าย', 'E24'),
('CL0056', 'ซื่อหลุง', 'เสื้อไว้สวมตัวที่ 3 มีลักษณะเป็นเสื้อสวมทับแขนยาว ไม่มีปก ผ่าหน้าตรง ความยาวของเสื้อจะยาวประมาณ 2ใน 3ของความยาวช่วงบนของผู้สวมใส่', 'E24'),
('CL0057', 'ชุดไตหย่า(ชาย)', 'กางเกงขาตรงสีดำหรือสีคราม เสื้อคอจีนแขนยาวสีดำ ไม่ประดับลวดลายใดๆ ทรงผมตัดสั้นตามสมัยนิยม และผู้ชายสามารถใช้ชุดประจำกลุ่มไตหย่าได้เมื่ออายุครบ 18ปี', 'E24'),
('CL0058', 'ชุดไทขึน(หญิง)', 'นิยมสวมเสื้อปั๊ด  นุ่งผ้าซิ่นต๋าลื้อลวดลายสวยงาม  และมักโพกศีรษะด้วยผ้าสีอ่อนเช่นสีขาวหรือสีชมพูเรียกวิธีการโพกหัวแบบไทเขินว่า  “การเคียนหัว” นอกจากนี้ยังมีการสะพายถุงย่ามและผ้าเช็ด หรือผ้าพาดบ่า  ซึ่ง ถือว่าเป็น ส่วนสำคัญประกอบ 3 อย่างที่ขาดไม่ได้ในการแต่งตัวตามแบบประเพณีนิยมของชุดไทเขิน', 'E25'),
('CL0059', 'ชุดไทขึน(ชาย)', 'นิยมสวมเสื้อแขนยาวคอกลมผ่าหน้า และใช้กระดุมผ้าแบบของชาวจีน  นุ่งกางเกงเตี่ยวโย้งเหมือนชาวจีน  มักต่อหัวกางเกงด้วยผ้าสีขาว เรียกว่า  “เตี่ยวหัวขาว”บางครั้งอาจมีการสวมใส่กางเกงม่อฮ่อม ขายาว ในวันที่ไม่ได้มีพิธีการใดใด  โพกศีรษะเหมือนผู้หญิง  แต่ผู้ชายนิยมใช้ผ้าสีขาวเป็นหลัก อาจมีสีชมพูบ้างในบางโอกาส และต้องไม่ลืมที่จะสะพายถุงย่าม และพกผ้าเช็ดหน้าเช่นเดียวกับผู้หญิง', 'E25'),
('CL0060', 'ชุดโส้(หญิง)', 'ใส่เสื้อสีดำแขนกระบอกเลยข้อศอกลงมาเล็กน้อย ผ่าอก ขลิบแดง ปล่อยให้เลยชายเสื้อลงไป 2-3 นิ้ว กระดุมเงินกลม ชายเสื้อด้านข้างแหวกชายสองข้างให้ด้ายแดงตกแต่งชายเสื้อ ขอบเสื้อและปลายแขน นุ่งผ้าฝ้ายมัดหมี่สีน้ำเงิน-ขาว ต่อตีนซิ่น หัวซิ่นด้วยผ้าแถบสีสันสวยงาม ทรงผมของผู้หญิงจะเกล้ามวยสูง (มะยวล) งานพิธีจะนุ่งผ้าไหม ใส่กำไลข้อมือข้อเท้า สร้อยเงิน ตุ้มหู ห่มสไบ', 'E69'),
('CL0061', 'ชุดโส้(ชาย)', 'แต่งกายเหมือนไทยลาว นุ่งผ้ากะเตี่ยว หากเรียนอาคมด้วยก็จะสวมลูกประคำแก้ว', 'E69'),
('CL0062', 'ชุดชาวบรู(หญิง)', 'แต่งกายคล้ายกับชาวอีสานทั่วๆ ไป คือ นุ่งผ้าถุง เสื้อแขนสั้น หรือเสื้อหมากกะแหล่ง', 'E39'),
('CL0063', 'ชุดชาวบรู(ชาย)', 'นุ่งกางเกง ชาวบรูไม่มีเอกลักษณ์ในการแต่งกายเพราะไม่มีการทอผ้าใช้เอง แต่นิยมซื้อเสื้อผ้าจากตลาด', 'E39'),
('CL0064', 'ชุดไทยโคราช(หญิง)', 'นุ่งโจงกระเบน เสื้อแขนกระบอก ห่มสไบทับเสื้อ ถ้าอยู่บ้านจะใช้ผ้าผืนเดียวคาดอก ', 'E26'),
('CL0065', 'ชุดไทยโคราช(ชาย)', 'นุ่งโจงกระเบน เสื้อคอกลมไม่ผ่าอก ผ้าขาวม้าคาดพุง', 'E26'),
('CL0066', 'เสื้อฮี', 'เสื้อที่ใช้สวมใส่ในพิธีกรรม มีการปักลวดลายตกแต่งด้วยเส้นไหมสีฉูดฉาดลงบนผืนผ้าสีดำด้วยผ้าไหมชิ้นเล็กๆ', 'E28'),
('CL0067', 'ซิ่นแตงโม', 'ทอด้วยฝ้าย ใช้นุ่งกันทุกโอกาส ซิ่นแตงโมแบ่งออกเป็น 3 ส่วน คือส่วนที่ 1 คือหัวซิ่นจะเป็นสีดำ หรือสีคราม ไม่มีลวดลาย ส่วนที่ 2 คือตัวซิ่นจะทอสลับกับสีขาวเป็นลายทางยาวตรงลงมา มองดูคล้ายกับผลแตงโม ส่วนที่ 3 มีลวดลายสีขาวสองสามริ้ว เย็บติดเป็นตีนซิ่น ส่วนนี้หากสตรีคนใดที่สามีเสียชีวิตต้องเลาะเอาส่วนตีนซิ่นออกทั้งหมดเพื่อเป็นสัญลักษณ์แห่งการไว้ทุกข์ให้กับสามี', 'E28'),
('CL0068', 'ชุดไทใหญ่(ชาย)', 'สวมเสื้อไต นุ่งกางเกงขาก๊วย มีผ้าเคียนศีรษะ', 'E37'),
('CL0069', 'ชุดไทใหญ่(หญิง)', 'สวมผ้าซิ่น หรือผ้าถุงยาวคลุมตาตุ่ม เสื้อแขนยาวคลุมเอว สาบเสื้อป้ายทับไปด้านเดียวกับผ้าถุงบ้าง และเป็นแบบหลวมๆ แต่ปัจจุบันมีการประยุกต์เป็นแบบรัดรูปเสื้อสั้น สาบเสื้อก็สลับทับไปคนละด้านบ้าง', 'E37'),
('CL0070', 'ชุดไทลื้อ(หญิง)', 'นิยมสวมเสื้อปั๊ดสีเข้ม เป็นเสื้อที่ไม่มีกระดุม แต่จะผูกเชือกที่สาบเสื้อด้านซ้าย นุ่งซิ่นต๋าลื้อ อาจมีเพิ่มลวดลายด้วยเทคนิคต่าง ๆ เพื่อความสวยงาม เกล้ามวยผมไว้บนกระหม่อมและเคียนหัวด้วยผ้าพื้นสีขาวหรือสีชมพู นิยมสะพายถุงย่าม หากไปวัดจะพาดบ่าด้วยผ้าเช็ดทุกครั้ง', 'E36'),
('CL0071', 'ชุดไทลื้อ(ชาย)', 'นิยมสวมเสื้อแขนยาว สวมทับด้วยเสื้อปาหรือเสื้อกั๊กปักลวดลายด้วยเลื่อม สวมกางเกงขายาวสีเดียวกับเสื้อ ต่อหัวกางเกงด้วยผ้าสีขาว เคียนหัวด้วยผ้าพื้นสีขาวหรือสีชมพู นิยมสะพายถุงย่าม หากไปวัดจะพาดบ่าด้วยผ้าเช็ดทุกครั้ง', 'E36'),
('CL0072', 'ชุดชาวกะซอง(หญิงสูงวัย)', 'ส่วนมากผู้หญิงสูงวัยจะสวมผ้านุ่ง สวมเสื้อคอกระเช้าและสวมเสื้อแขนสั้นหรือแขนยาว ทรงกระบอกทับอีกชั้นหนึ่ง', 'E02'),
('CL0073', 'ชุดชาวกะซอง(ชาย)', 'แต่งตัวตามสมัยนิยมไม่ต่างจากคนไทยทั่วไป ชาวกะซองไม่มีการปั่นฝ้ายหรือทอผ้าไว้ใช้เอง เสื้อผ้าเครื่องแต่งกายหาซื้อได้จากตลาด ', 'E02'),
('CL0074', 'ชุดชาวซะโอจ(หญิง)', 'จะนุ่งผ้าถุงสีดำ  เสื้อแขนยาว', 'E16'),
('CL0075', 'ชุดชาวซะโอจ(ชาย)', 'นิยมนุ่งกางเกงขาก๊วยสีดำ  เสื้อแขนยาว ', 'E16'),
('CL0076', 'ชุดชาวซำเร', 'สมัยก่อนนุ่งผ้าเตี่ยวตัดจากกระสอบ เรียกว่า \"คาดเตี่ยว\" ปัจจุบัน แต่งกายแบบคนทั่วไป ผู้หญิงนุ่งผ้าซิ่น ผู้ชายนุ่งกางเกง', 'E17'),
('CL0077', 'ฮีญาบ', 'เป็นผ้าคลุมผมของผู้หญิงชาวอิสลาม ที่มีการปกปิดไว้ตั้งแต่ผมส่วนบนลงมาจนคลุมหน้าอก เพื่อแสดงออกถึงความสำรวม', 'E32'),
('CL0078', 'ชดบานงมาตรฐาน', 'กระโปรงตัดเย็บด้วยผ้าปาเต๊ะ(ผ้าพัน)ที่มีลวด\nลายสวยงามมาตัดเย็บเป็นกระโปรงจีบหน้านาง เสื้อตัดเย็บด้วยผ้าต่วนสีน้ำตาลแดงเข้าชุดกับสีลายปาเต๊ะ แขนยาวรัดรูป ปลายแขนบาน 2 ชั้น ติดกระดุมเป็นเข็มกลัดรูปผีเสื้อ 3 เม็ด คลุมศีรษะด้วยผ้าคลุมลูกไม้สีน้ำตาลอ่อน ประดับด้วยเข็มกลัดรูปดอกไม้สีฟ้าอ่อน บ่งบอกถึงความเป็นเอกลักษณ์ในการแต่งกายของสตรีมุสลิมชายแดนใต้ได้อย่างสวยงสมและดูมีเสน่ห์ยิ่งนัก', 'E32'),
('CL0079', 'ชดบานงแมแด', 'ใช้ผ้าไนลอนหรือผ้าใยสังเคราะห์ตัดเย็บเป็นตัวเสื้อและกระโปรงเข้าชุดกันด้วยผ้าสีเขียวอ่อนและปักตกแต่งลวดลายที่แขนเสื้อ ตัวเสื้อปักตกแต่งตั้งแต่สาบเสื้อรอบคอที่มีลักษณะเป็นคอสามเหลี่ยมยาวจนถึงชายเสื้อ', 'E32'),
('CL0080', 'ชุดกูรงแบบที่ 1', 'เสื้อและกระโปรงตัดเย็บด้วยซอแก๊ะหรือผ้าปักดิ้นเงินดิ้นทองสีน้ำตาล ปักลวดลายโบราณที่มีความสวยงามเหมาะสำหรับสวมใส่ไปงานที่เป็นพิธีการ', 'E32'),
('CL0081', 'ชุดกูรงแบบที่ 2', 'ตัดเย็บด้วยผ้าชีฟองสีส้มอ่อนมีลวดลาย ดอกสีม่วงอ่อน สีแดงเข้าชุดกันทั้งตัวเสื้อและกระโปรงความยาวคลุมเข่าคลุมศีรษะด้วยผ้าคลุมผมผ้าป่านสีม่วงอ่อนดูสดใสสวยงามอย่างมีเสน่ห์', 'E32'),
('CL0082', 'เสื้อคุยเฮง', 'ไม่มีคอมีรูปทรงหลวมๆ เป็นผ้าฝ้าย สีของเสื้อนิยมใส่สีทึบ ', 'E27'),
('CL0083', 'ปั๋งลิ้น', 'คนที่มีฐานะเวลาออกงานจะใส่เสื้อผ้าแพร', 'E27'),
('CL0084', 'หมวกก้วยโล้ย', 'นิยมสวมหมวกที่สานจากไม้ไผ่หรือด้วยจากแล้วลงด้วยน้ำมันยางเพื่อกันน้ำฝนมีทรงแหลมสูง ปีกหมวกกว้าง', 'E27'),
('CL0085', 'ชุดชาวไทยเบิ้ง(หญิง)', 'นิยมตัดผมทรงดอกกระทุ่ม ใส่เสื้อเสื้ออีหิ้ว ที่มีลักษณะเหมือนเสื้อสายเดี่ยว นุ่งผ้าโจงกระเบนหรือผ้าโต่ง (ผ้าถุง) มีผ้าขาวม้าเป็นสไบเฉียง', 'E29'),
('CL0086', 'ชุดชาวไทยเบิ้ง(ชาย)', 'ใส่เสื้อคอกลมผ้าป่าน นุ่งผ้าโจงกระเบน หรือกางเกงขาก๊วย บางคนนุ่งผ้าตาหมากรุก มีผ้าขาวม้าพาดบ่าหรือเคียนเอว และสะพายย่ามอันเป็นเอกลักษณ์  แถมยังนามสกุลยังมีคำว่า \"สลุง\" อีกด้วย', 'E29'),
('CL0087', 'ราชปะแตน', 'เครื่องแต่งกายชายไทย ประกอบด้วยเสื้อสูทสีขาว คอตั้งสูง และมีกระดุมห้าเม็ด โจงกระเบน สวมถุงเท้ายาวถึงเข่า และรองเท้าหุ้มส้น', 'E31'),
('CL0088', 'ชุดไทยภาคกลาง(หญิง)', 'นิยมสวมใส่ผ้าซิ่นยาวครึ่งแข้ง ห่มสไบเฉียงตามสมัยอยุธยา ทรงผมเกล้าเป็นมวย และสวมใส่เครื่องประดับเพื่อความสวยงาม', 'E31'),
('CL0089', 'ชุดชาวไทโย้ย(หญิง)', ' เสื้อ แต่งกายด้วยผ้าย้อมคราม เป็นเสื้อแขนกะบั้ง (เสื้อแขนกระบอก) ,เสื้อพวง\n(เสื้อคอกระเช้า) , เสื้อต่อง (คล้ายเสื้อสายเดี่ยวในปัจจุบัน) สีขาวก็นิยมใส่ให้เห็นแต่น้อยกว่า\nย้อมคราม ดังนั้น ยุคนั้นทุกครัวเรือนต้องทำไร่ฝ้ายและสวนคราม\nผ้าซิ่น (ผ้าถุง) จะนุ่งผ้ามัดหมี่ที่ต่อหัว (ผ้าที่นำมาต่อด้านบนของผ้าถุง) ต่อตีน ( ผ้าที่นำมา\nต่อ-ข้างล่างของผ้าถุง ซึ่งทอด้วยลวดลายที่สวยงาม) ถือเป็นชุดเอาบุญ ที่ไม่ต่อก็นุ่งทั่วไป', 'E34'),
('CL0090', 'ชุดชาวไทโย้ย(ชาย)', 'เสื้อ เป็นเสื้อคอกลมแขนกว้าง แขนสั้นหรือยาวก็ทรงแขนกว้างเหมือนกัน\nย้อมด้วยคราม แพเลงมัดแอว (ผ้าขาวม้าคาดพุง)\nกางเกง เรียกว่า “ส่งฮั่ง” ถ้าเป็นเด็กกลัวหลุด ก็ใช้ด้ายสอดมัด เรียกว่า “ส่ง\nหัวฮูด”\nผ้าสะโหร่งก็นิยมใช้ จะทอด้วยไหม สมัยนั้นชาวไทโย้ยยังนิยมเลี้ยงหม่อน แต่จะพากันเรียกว่า\nเลี้ยงไหม', 'E34'),
('CL0091', 'ชุดไทโย้ย', 'แต่งกายเป็นชุดพื้นเมือง เป็นผ้ามัดย้อมครามลายมัดหมี่ โทนสีจะเป็นสีขาว สีคราม และก็สีกรมท่า', 'E35'),
('CL0092', 'ชุดเอาบุญ (ภาษาโย้ย)', 'ชุดที่ชาวโย้ยนิยมแต่งกันเฉพาะในวันสำคัญ\nเช่น งานบุญประจำปี งานแต่งงาน งานทำบุญขึ้นบ้านใหม่ เป็นต้น', 'E35'),
('CL0093', 'ดัสตาร(Dastar) ', 'สิ่งที่สำคัญชายชาวซิกข์จะต้องโพกผ้าศีรษะ', 'E38'),
('CL0094', 'ชุดซัลวาร์-กามีซ (SalwarKameez) ', 'คือเสื้อมีแขน ความยาวของเสื้อถึงเข่าหรือยาวกว่าและกางเกงหูรูดขายาว สีสันและเนื้อผ้าให้เป็นไปตาม\nสมัยนิยมและรสนิยมของผู้สวมใส่', 'E38'),
('CL0095', 'ดูบัดตา (Dubatta)', 'ผ้าคลุมศีรษะ สีสันและแพรพรรณก็เช่นเดียวกันให้เป็นไปตามความชอบของผู้สวมใส่ แต่จะต้องกว้างยาวพอที่จะคลุมศีรษะ\nได้มิดชิด ดูบัดตาที่ได้กล่าวข้างต้นนี้ไม่ใช่เป็นผ้าคลุมใบหน้าเป็นเพียงแต่ผ้าคลุมศีรษะเท่านั้น ซึ่งชาวซิกข์ใช้เป็น\nการแสดงความเคารพต่อบุคคลและสถานที่ในขณะนั้น อีกทั้งยังเป็นศักดิ์ศรีของผู้สวมใส่', 'E38'),
('CL0096', 'ชุดชาวบีซู(ชาย)', 'นิยมใส่เสื้อแขนยาวสีแดงทำจากผ้าฝ้าย ผ้าโพกหัวสีแดง เพราะสีแดงเป็นสัญลักษณ์ของความแข็งแกร่ง และจะไม่มีการปักลวดลายบนผ้า', 'E40'),
('CL0097', 'ชุดชาวบีซู(หญิง)', 'นิยมใส่เสื้อปักแขนยาว นิยมใส่สีน้ำเงิน ผ้าซิ่น มักจะทอเป็นลายก่านคอควาย ใส่สีดำ แดง ขาว ตีนซิ่นจะเป็นสีเขียว เอกลักษณ์ของชาวบีซูจะเอาเบี้ยมาตกแต่งผ้า', 'E40'),
('CL0098', 'ชุดชาวปลัง', 'การแต่งกายของผู้ชายและผู้หญิงคล้ายกัน ผู้หญิงจะนุ่งผ้าซิ่น (ผ้าถุง) ในภาษาปลังเรียกว่า “โรงด้วย” คำว่า “โรง” แปลว่าสีดำ ดังนั้น ผ้าซิ่นที่ผู้หญิงนุ่งจึงมีสีดำ ผ้านุ่งแบ่งออกเป็น 3 ตอนคือ หัวซิ่น ตัวซิ่น และชายซิ่น ในภาษาปลังหัวซิ่นเรียกว่า “ด้าย เซมลือ” แปลว่า “ผ้าซิ่น ไทลื้อ” ', 'E42'),
('CL0099', 'ชุดชาวปะโอ(ชาย)', 'แต่งกายคล้ายชาวไทใหญ่ สวมกางเกงเป้าหย่อนที่เรียกว่า เตี่ยวโหย่ง หรือโก๋นโฮง เสื้อคอกลมแขนกระบอก ผ่าอกตลอด กระดุมผ้าสอดเป็นห่วงแบบจีน  ชายเสื้อคลุมตะโพก คล้ายเสื้อกะเหรี่ยง เสื้อชั้นนอกสวมทับ แขนยาว เอวลอย ผ่าอกตลอด คอจีน มีลายแดงยาวพาดขวางเป็นระยะ ', 'E43'),
('CL0100', 'ชุดชาวปะโอ(หญิง)', ' นุ่งผ้าถุงสีดำยาว แต่งคล้ายกะเหรี่ยง ทำด้วยผ้าหนา คอเป็นรูปตัว V แขนเสื้อซ้อนกัน 2 ตอน ใช้สีดำเป็นสีพื้นมีลายแดงเป็นทางลงมา ยาวลงมาครึ่งขา ส่วนผ้านุ่งใช้สีดำกว้างแต่สั้นขนาดหัวเข่าโพกผ้าสีดำ เครื่องประดับกายมีตุ้มหู กำไลมือสร้ายคอ ทำด้วยโลหะเงิน บางกลุ่มสวนกำไรทองเหลือง', 'E43'),
('CL0101', 'ปาเต๊ะซารุง (sarongs) หรือโสร่ง', 'หญิงสาวที่อายุยังน้อย มักจะชอบสวมใส่ซิ่นสีสันสดใส  หลังจากที่นุ่งผ้าซารุงเรียบร้อยแล้ว kepala kain จะน ามาใช้รัดรอบเอวอย่าง\nประณีตโดยหันออกด้านหน้า', 'E44'),
('CL0102', 'เคบายา (Kebaya)', 'เสื้อบางที่มีการปักลายปักฉล มักจะสวมใส่คู่กับ  ผ้าปาเต๊ะซารุง', 'E44'),
('CL0103', 'ชุดไทพวน(หญิง)', 'จะนุ่งซิ่นเทคนิคยกมุก เป็นเส้นยืนพิเศษ ต่อตีนซิ่นด้วยจก แต่เดิมจะเคร่งครัดเรื่องแต่งกาย คือ หญิงสาวโสดจะนุ่งซิ่นตีนแดง คือ ตีนจกพื้นแดงเคียนอก (เรียกว่า แฮงตู้) ด้วยผ้ามีสีสันงดงาม หญิงแต่งงานแล้วจะนุ่งซิ่นตีนดำ คือ ซิ่นตีนจกพื้นดำ เคี่ยนอกด้วยผ้าย้อมมะเกลือหรือคราม', 'E46'),
('CL0104', 'ชุดไทพวน(ชาย)', 'จะนุ่งชุดประจำวัน เป็นเสื้อผ้าฝ้ายย้อมครามหรือมะเกลือคอกลม ผ่าหน้า ผูกเชือกแขนสามส่วน เช่นเดียวกับชาวไทยยวน นุ่งกางเกงครึ่งแข้ง ย้อมคราม หรือมะเกลือ มีผ้าขาวม้าคาดเอว ส่วนชุดไปงานพิธี จะสวมเสื้อผ้าฝ้ายคอตั้งแขนยาว นุ่งโจงกระเบนแบบทางกรุงเทพ ฯ', 'E46'),
('CL0105', 'ชุดคนใต้(หญิง)', 'จะห่มผ้าซีก (ผ้าแถบ) “ฉ้อคอ” อย่างห่มตะแบงมาน ชายผ้าซีกทั้งสองข้างผูกไว้ที่ต้นคอ ชายหางผ้าซีกยาวพาดบ่าอย่างสไบเฉียง หรือใช้ผ้าห่มรัดอกผืนหนึ่ง และห่มสไบเฉียงซ้อนอีกผืนหนึ่ง นุ่งผ้าโจงกระเบนเป็นผ้าพื้น ถ้าเป็นงานพิธีบุญจะนุ่งผ้ายกฝ้าย ผู้มีฐานะจะนุ่งยกดิ้นเงินทอง', 'E13'),
('CL0106', 'ชุดคนใต้(ชาย)', 'จะนุ่งผ้าพื้นเลื้อยชาย หรือนุ่งผ้าโสร่ง ถ้ามีงานพิธีจะนุ่งผ้าโจงกระเบนไม่ใส่เสื้อ ใช้ผ้าขาวม้าหรือผ้าห่มพาดบ่า ถ้ามีฐานะจะใช้ผ้าซักอาบ (ผ้าขาวม้า) ทอด้วยไหม ห่มสะพายเฉียง', 'E13'),
('CL0107', 'ชุดชาวมลาบรี(ชาย)', 'จะใช้เปลือกไม้มานุ่งหรือผ้าเตี่ยวพันส่วนล่างเรียกว่าผ้า“ตะแหย้ด”ไม่สวมเสื้อ', 'E49'),
('CL0108', 'ชุดชาวมลาบรี(หญิง)', 'สวมชุดที่ทำจากเปลือกไม้ ปัจจุบันแต่งกายตามปกติเหมือนคนทั่วไป', 'E49'),
('CL0109', 'ชุดมลายู หรือ Baju Budaya Melayu', 'โดยผู้ชายจะสวมชุดตะโละบลางอ ส่วนผู้หญิงจะสวมชุดกูรง', 'E50'),
('CL0110', 'ชุดชาวมอแกน', 'มอแกนไม่มีเครื่องแต่งกายหรือชุดประจำเผ่าที่เป็นเอกลักษณ์ เสื้อผ้าเครื่องแต่งกายของมอแกนเป็น แบบเรียบง่าย มีเพียงเสื้อ กางเกง หรือผ้านุ่ง ผ้าขาวม้าแบบคนในท้องถิ่นภาคใต้', 'E51'),
('CL0111', 'ชุดชาวมานิแบบดั้งเดิม', 'จะนุ่งเปลือกไม้และตะไคร่น้ำที่เกาะเป็นแผ่นตามก้อนหินใหญ่ ๆ ในป่า โดยนำตะไคร่น้ำนี้มาตากให้แห้งและถักทอเป็นเครื่องนุ่งห่ม หญิงชาวมานิจะนุ่งยาวประมาณเข่าหรือครึ่งน่อง คาดอกหรือเปลือยอก ผู้ชายนุ่งสั้นแค่เข่าและเปลือยท่อนบน ส่วนเด็ก ๆ จะไม่นุ่งอะไรเลย นอกจากนี้ยังมีการนำใบไม้มาปกปิดร่างกาย โดยใช้ใบเตยหนามมาทำเป็นเครื่องนุ่งห่มด้วยการเอาหนามที่ใบเตยออกโดยใช้ไม่ไผ่บาง ๆ มาตัดหนามออกแล้วนำมาตากแดด จากนั้นก็นำไปสานเป็นเครื่องนุ่งห่ม ใบเตยหนามสามารถหาได้จากบริเวณที่ตั้งทับ ', 'E54'),
('CL0112', 'ชุดชาวยะไข่(ชาย)', 'แต่งกายด้วยเสื้อคอจีนแหล่กะโด โสร่งหรือปะโซที่นุ่งก็ต้องทออย่างประณีตและชอบลวดลายที่เป็นเอกลักษณ์ ศีรษะก็ต้องสวมผ้าโพกสำเร็จรูป', 'E55'),
('CL0113', 'ชุดชาวยะไข่(หญิง)', 'นิยมเกล้าผมซึ่งมีวิธีเกล้าหลายแบบด้วยกัน ผ้าถุงหรือทะเม่ยนั้นก็ต้องทอแบบประณีตเป็นพิเศษและนิยมลายขวาง', 'E55'),
('CL0114', 'ชุดชาวลัวะ(หญิง)', 'นิยมสวมเสื้อสีขาวหรือสีดำแขนสั้นกุ๊นด้วยด้ายสี นุ่งซิ่นสั้นครึ่งเข่าสีดำมีลายคั่นเป็นแถบสีแดง ชมพู  และน้ำเงินแซมขาว  ซึ่งได้จากการมัดย้อมหรือปั่นไก พันแขนด้วยผ้าปอเต๊ะ พันขาด้วยผ้าปอซวง สตรีชาวลัวะนิยมแสกกลางศีรษะมวยต่ำไว้ท้ายทอย ประดับมวยผมด้วยปิ่นขนเม่น สวมสร้อยเงินเม็ด สร้อยลูกเดือย สร้อยลูกปัดสีแดง สีส้ม สีเหลือง และใส่ตุ้มหูไหมพรมยาวถึงไหล่', 'E57'),
('CL0115', 'ชุดชาวลัวะ(ชาย)', 'นิยมสวมเสื้อแขนยาวสีขาวผ่าหน้า นุ่งกางเกงสะดอขาว เคียนหัวด้วยผ้าสีแดงหรือชมพู และพกมีดด้ามงาช้าง', 'E57'),
('CL0116', 'ชุดชาวลาวครั่ง', 'ผ้าถุงของสตรีชาวลาวครั่งมักจะเป็นสีแดง อันเป็นสีที่ได้จากการนำครั่งมาย้อมเส้นด้ายสอดรับกับชื่อเรียกขานชาติพันธุ์ว่า ลาวครั่งมีการทอยกดอกด้วยลวดลายต่าง ๆ แล้วกล่าวว่าเป็นลายประจำชาติพันธุ์ของลาวครั่ง', 'E58'),
('CL0117', 'ชุดชาวลาวแง้ว(หญิง)', 'หญิงจะนุ่งโจงกระเบน ถ้าอยู่บ้านก็จะใช้ผ้าขาวม้าคาดอกแบบตะแบงมาน แต่หากไปวัด จะใช้ผ้าคาดอกแล้วมีผ้าอีกผืนหนึ่ง ห่มทำสไบเฉียง หญิงสาวโสดนิยมใส่กำไลขา 2 ข้างตามฐานะ ถ้าฐานะดีใส่กำไลทอง ฐานะไม่ดีนักใส่กำไลเงิน และจะถอดกำไรออกเมื่อแต่งงาน นอกจากนี้ยังเจาะหูใส่ต่างหูทั้ง 2 ข้าง', 'E59'),
('CL0118', 'ชุดชาวลาวแง้ว(ชาย)', 'เวลาไปวัดจะนุ่งโจงกระเบนเช่นกัน แต่ถ้าอยู่บ้าน จะนุ่งกางเกงขาก๊วยใส่เสื้อคอกลมที่ตัดจากผ้าที่ทอเองและนิยมเจาะหูใส่ต่างหู 1 ข้าง', 'E59'),
('CL0119', 'ชุดชาวลาวตี้(หญิง)', 'แต่งกายด้วยเสื้อทอจากผ้าฝ้ายย้อมครามหรือย้อมดำ คอกลมติดคอหรือคอตั้ง ผ่าหน้าติดกระดุมเสื้อ แขนกระบอกหรือแขนยาว หรือเสื้อคอกระเช้า เสื้อห้าตะเข็บหรือใช้ผ้าแถบพันตัวเวลาอยู่บ้าน', 'E60'),
('CL0120', 'ชุดชาวลาวตี้(ชาย)', 'จะแต่งกายด้วยเสื้อกางเกงขาก๊วย ทอด้วยผ้าฝ้ายย้อมคราม หรือย้อมจากเปลือกต้นคาง หรือจามจุรี (ดอกสีแดงต้องนำไปหมักโคลนครึ่งวัน จึงจะให้สีดำ) ถ้าไปทำนา มักเคียนเอวด้วยผ้าขาวม้า ถ้าอยู่กับเรือนมักไม่สวมเสื้อ หรือใส่เสื้อผ้าป่านคอกลมแขนสั้น หรือแขนสามส่วน', 'E60'),
('CL0121', 'ชุดชาวลาวเวียง(ชาย)', 'นุ่งกางเกงหลวมๆขาครึ่งหน้าแข็ง คล้ายกางเกงจีน หรือกางเกงขาก๊วย ตามที่เรียกกัน แต่เอวจะแคบ\nกว่า สีของกางเกงจะเป็นสีดํา หรือสีวรรณะเข้ม อาจเป็นกรมท่าก็มีเสื้อคงเป็นเสื้อคอกลมหลวมๆมีกระดุม\nและไม่มีกระดุม อาจเป็นเสื้อป่าน หรือผ้าด้ายดิบ มักไม่มีลวดลาย ส่วนใหญ่สีขาว ถ้าไปงานบุญ สีกรมท่า น้ําเงินเข้ม ก็สวมใส่กันมาก ที่สําคัญคือ มีผ้าขาวม้าคาดเอวเพื่อความทะมัดทะแมง', 'E61'),
('CL0122', ' เสื้ออ๊อดสายเดียว', 'มีลักษณะเป็นผ้าดิบหรือผ้าลินินสีขาว สีอื่นมีให้เห็นน้อยมาก อาจเป็นสีดําไปเลยก็มีให้เห็นหนามาก เพราะมีกระเป๋าข้างหน้าสองใบ เหมือนกระเป๋าเสื้อเชิ้ตของบุรุษใน\nปัจจุบัน รัดรูปแน่น ติดกระดุมข้างเสื้ออ๊อดสายเดียว มีลักษณะเหมือนเสื้อคอกระเช้า แต่รัดรูปแน่น', 'E61'),
('CL0123', 'ชุดชาวลาวอีสาน(ชาย)', 'เมื่ออยู่กับบ้านจะนุ่งกางเกงขาก๊วยสั้น สวมเสื้อม่อฮ่อม แขนสั้น คาดผ้าขาวม้าตาตาราง เมื่อออกไปนอกบ้านเพื่อร่วมงานบุญ จะนุ่งโสร่ง สวมเสื้อคอกลมแขนสั้น มีผ้าขาวม้าคล้องคอ', 'E62'),
('CL0124', 'ชุดชาวลาวอีสาน(หญิง)', 'นิยมแต่งด้วยผ้าย้อมครามทั้งเสื้อและผ้าซิ่น', 'E62'),
('CL0125', 'ชุดชาวลีซู(หญิง)', 'แต่งกายด้วยผ้าสีสดใส สวมใส่เป็นเสื้อตัวหลวม แขนกระบอก ส่วนหน้าของเสื้อยาวถึงเข่าส่วนหลังยาวถึงนิยมสีน้ำเงิน เขียว ฟ้า และม่วง คอเป็นผ้าสีดำ ตกแต่งลวดลายสวยงาม โดยนำผ้าแถบผ้าสีต่างๆเช่น ม่วง ฟ้า ชมพู เขียว ดำ ขาว ส้ม แดง มาเย็บต่อกันเป็นริ้ว สวมเสื้อกั๊กที่ตกแต่งด้วยกระดุมเงิน สวมกางเกงหลวมๆสีดำ ในตัวเสื้อ ใช้ผ้าสีดำพันรอบเอว คล้ายเข็มขัด สวมปลอกขาเพื่อป้องกัน แมลง กิ่งไม้ หรือกันหนาว ผู้หญิงสูงวัยโพกหัวด้วยผ้าสีดำยาวพันหัวหลายๆรอบ แล้วเก็บชาย หญิงสาวจะสวมหมวกโดยประดับประดาด้วยลูกปัดหลากสี', 'E64'),
('CL0126', 'ชุดชาวลีซู(ชาย)', 'สวมใส่กางเกงที่มีความยาวเลยเข่าเล็กน้อย เป้ากว้างมาก สีน้ำเงิน สีเขียว ฟ้า เสื้อสีดำ แขนยาว คอป้ายตกแต่งกระดุมเงิน ติดรังดุมสีน้ำเงินที่ส่วนบนของตัวเสื้อ สวมปลอกขาสีดำ', 'E64'),
('CL0127', 'ชุดชาวเลอเวือะในชีวิตประจำวัน(หญิง)', 'เสื้อ สีขาวแขนกุ๊นด้วยด้ายสี\n\nซิ่น ผ้าพื้นสีดำ มีลายผ้าเป็นแถบยาวนอนคั่นตลอดผืนผ้า มีขนาดแนบพอดีตัว นิยมใส่กางเกงวอร์มในผ้านุ่ง\n\nปอเต๊ะ ผ้าพันแขน / ปอชวง ผ้าพันแข้ง\n\nทรงผม นิยมไว้ยาว ม้วนมวยประดับปิ่นเงิน\n\nเครื่องประดับ สร้อยลูกปัดหลากสี / โบระ (ต่างหูเงินรูประฆังหงาย) / สกุนลอง (กำไลสวมแข้งเส้นเล็กทำด้วยฝ้ายเคลือบยางรัก) / สกุนเลีย (กำไลทองเหลือง) ฯลฯ', 'E65'),
('CL0128', 'ชุดชาวเลอเวือะในชีวิตประจำวัน(ชาย)', 'ปัจจุบันนิยมแต่งกายอย่างคนพื้นราบ\n\nสะพายย่ามไม่ปักลวดลาย ที่มีจุดเด่นที่การเย็บรอยตะเข็บต่าง ๆ อย่างประณีต', 'E65'),
('CL0129', 'ชุดชาวเลอเวือะในพิธีกรรม(หญิง)', 'พิธีเลี้ยงผีฟ้าผ่า แต่งงาน งานศพ สวมเสื้อสีดำทับสีขาว\n\nใช้ผ้าสีแดง เหลือง ขาวคลุมหน้า', 'E65'),
('CL0130', 'ชุดชาวเลอเวือะในพิธีกรรม(ชาย)', 'สวมเสื้อแขนยาวสีขาวทับเสื้อยืด เหน็บมีดงาช้างข้างลำตัว โพกศีรษะด้วยผ้าแดง / ชมพู\nถ้าเป็นเจ้าบ่าวนุ่งผ้าคล้ายโจงกระเบนทับกางเกง', 'E65'),
('CL0131', 'อ่าวหญ่าย (Ao dai)', 'ชุดผ้าไหมที่พอดีตัวสวมทับกางเกงขายาวซึ่งเป็นชุดที่มักสวมใส่ในงานแต่งงานและพิธีการสำคัญของประเทศ มีลักษณะคล้ายชุดกี่เพ้าของจีน', 'E66'),
('CL0132', 'ชุดชาวแสก(ชาย)', 'เสื้อดำแขนสั้น ผ้ายอมหม้อสีคราม เสื้อคอกลมติดกระดุมด้านหน้ากางเกงขาก๊วย หรือขาครึ่งท่อ ผ้าคาดเอว เป็นผ้าขาวม้า เป็นลายตะล่องสีแดง ส่วนผ้าพาดบ่าใช้ผ้าสีแดงล้วน', 'E68'),
('CL0133', 'ชุดชาวแสก(หญิง)', 'เสื้อแขนยาวสีดำ ผ่าอก ติดกระดุมด้านหน้า ผ้าถุงสีดำมีเชิงที่ปลายผ้าถุง ผ้าถุงยาวคลุมเท้าผ้าคาดเอวนิยมเป็นผ้าลายเดียวกันกับลายเชิงผ้าถุงผ้าเบี่ยงซ้าย นิยมใช้ผ้าสีแดง นิยมใส่ตุ้มหู กำไลขา สร้อยขา และสร้อยคอ นิยมไว้ผมยาวเกล้ารัดมวย', 'E68'),
('CL0134', 'ชุดชาวอาเค๊อะ(หญิง)', 'เมื่ออยู่บ้านผู้หญิงจะสวมผ้าคาดอก (เรียกว่า บิหะ) มีการประดับลายและผ้าสีให้สวยงาม นุ่งผ้าถุง ซึ่งชุดที่ใส่ในงานประเพณีเป็นผ้านุ่งที่เย็บชายด้วยผ้าสีเขียวเข้ม และสวมที่ครอบขา เมื่อออกจากบ้านหรือยามอากาศเย็นก็จะใส่เสื้อแขนกุดตัวสั้น และสวมเสื้อคลุมแขนยาว มีการเย็บลวดลายที่ปลายแขนเสื้อและชายเสื้อ นิยมสวมสร้อยเงิน หรือห่วงเงิน และลูกปัดขนาดใหญ่สีแดง น้ำเงิน และเหลืองเป็นเครื่องประดับ ยิ่งไปกว่านั้น เอกลักษณ์ที่โดดเด่นของผู้หญิงอาเค๊อะ คือ การโพกผ้าคลุมศีรษะสีดำ เครื่องประดับศีรษะ และการเจาะหูเพื่อใส่ต่างหูขนาดใหญ่ ลักษณะการโพกผ้าคลุมศีรษะมีลักษณะที่แตกต่างไปตามสายตระกูลและวัยด้วย', 'E71'),
('CL0135', 'ชุดชาวอาเค๊อะ(ชาย)', 'ใส่กางเกงขากว้างและเสื้อคลุมแขนยาวสีดำแบบจีน สะพายย่ามที่มีลวดลายเฉพาะแบบอาเค๊อะ', 'E71'),
('CL0136', 'ชุดชาวอูรักลาโวยจ(ชาย)', 'นุ่งผ้าขาวม้าแบบชาวไทยพุทธ หรือนุ่งกางเกงแบบชาวจีน (กางเกงเล) ผ้าขาวม้าคาดเอว เปลือยท่อนบน', 'E73'),
('CL0137', 'ชุดชาวอูรักลาโวยจ(หญิง)', 'นุ่งผ้าปาเต๊ะกระโจมอก', 'E73'),
('CL0138', 'ชุดชาวยุมบรีดั้งเดิม', 'ทำขึ้นมาจากเส้นใยและบางครั้งก็เป็นใบไม้ตามป่า นั่นคือ “อะกูกู้ลาม” หรือเปลือกต้นไม้ แต่อาจจะใช้ “อะกูกู้โยน-ร” หรือกาบกล้วยก็ได้แต่ใช้ได้ไม่นานเท่าเปลือกไม้ บางครั้งก็ใช้เปลือกของต้นปอสาหรือกาบกล้วยที่รัดติดกันด้วยเชือกที่ทำจากเถาวัลย์ ', 'E56'),
('CL0139', 'ชุดชาวโพล่ง(หญิงโสด)', 'สวมชุดทรงผ้าฝ้ายพื้นขาว ทอหรือปักประดับลวดลายให้งดงาม', 'E47'),
('CL0140', 'ชุดชาวโพล่ง(หญิงแต่งงาน)', 'สวมใส่เสื้อสีดำ น้ำเงินและนุ่งผ้าถุงสีแดงคนละท่อนเท่านั้น ตกแต่งด้วยลูกเดือย (เชื่อกันว่าป้องกันผีได้) หรือทอยกดอก ยกลาย ห้ามกลับไปสวมใส่ชุดยาวสีขาวอีกเพราะเป็นการดูถูกความบริสุทธิ์ของหญิงสาว', 'E47'),
('CL0141', 'ชุดชาวโพล่ง(ชาย)', 'สวมกางเกงสีดำและน้ำเงินหรือกรมท่า เสื้อจะยาวลงไปคลุมสะโพกและอาจยาวถึงเข่า', 'E47'),
('CL0142', 'ชุดกะหร่าง', 'นิยมใช้ผ้าฝ้ายทำเสื้อ ผ้าย้อม และทอเอง ปัจจุบันใช้ด้ายสีเคมีสีฉูดฉาดมา ตกแต่ง ปักด้วยเมล็ดพืชเป็นลวดลายสลับกับด้ายที่มัดเป็นเปลาะ ๆ', 'E05'),
('CL0143', 'กี่เพ้าหรือฉีผาว', 'หมายถึงชุดเสื้อคลุมยาว เป็นงานตัดเย็บที่รวมเอาศิลปะหลายแขนงไว้ด้วยกัน ไม่ว่าจะเป็นเทคนิคการอำพรางจุดด้อยบนร่างกาย หัตถกรรมการปักลวดลาย ภาพดอกไม้และนก หรือภาพอื่นๆ ซึ่งล้วนสะท้อนให้เห็นถึงเอกลักษณ์ทางวัฒนธรรมของจีนได้เป็นอย่างดี', 'E15'),
('CL0144', 'ถังจวงหรือเสื้อคอจีน', 'เสื้อถังจวงมีลักษณะเด่น 4ประการคือ\n1. คอเสื้อตั้ง โดยเปิดคอเสื้อด้านหน้าตรงกลางไว้\n2. แขนเสื้อและตัวเสื้อเป็นผ้าชิ้นเดียวกัน จึงไม่มีรอยตะเข็บต่อระหว่างแขนเสื้อและตัวเสื้อ\n3. สาบเสื้อเป็นแนวตรงหรือแนวเฉียง\n4. กระดุมเสื้อเป็นกระดุมแบบจีนซึ่งประกอบด้วยเม็ดกระดุมที่ใช้ผ้าถักเป็นปมและห่วงรังดุม', 'E14'),
('CL0145', 'ชุดผ้าลายสุวรรณวัชร์', 'ลายผ้าที่ถอดแบบลายจากลายบนต้นเสาศาลาการเปรียญของวัดใหญ่สุวรรณาราม อ.เมือง จ.เพชรบุรี', 'E30'),
('CL0146', 'ชุดชาวกะโผล่ว', 'ชุดกระโปรงของหญิงกะเหรี่ยง นำผ้าที่ได้จากการทอผ้าแบบคาดหลังซึ่งเป็นผ้าหน้าแคบ จึงต้องนำผ้าที่ได้มาพับครึ่งเย็บต่อกัน โดยเว้นหัวเว้นแขนทั้ง 2 ข้างไว้โดยไม่มีส่วนโค้งส่วนเว้า ส่วนผ้าซิ่นจะนำผ้ามาเย็บ ต่อกันประมาณ 2-3 ชิ้นขึ้นอยู่กับลวดลาย และขนาดตัวของผู้สวมใส่ หญิงสาวกะเหรี่ยงที่แต่งงานแล้วกับหญิงที่ยังไม่แต่งงานจะมีการแต่งกายที่มีลักษณะเฉพาะที่ต่างกัน โดยเฉพาะการประดับตกแต่งลวดลายบนเสื้อผ้า ', 'E03'),
('CL0147', 'ชุดชาวมอแกลน', 'ชาวเลทุกกลุ่มไม่มีเครื่องแต่งกายที่เป็นอัตลักษณ์ประจาเผ่าหรือชาติพันธุ์ เพราะในสภาพอากาศร้อน ชื้นและวิถีชีวิตที่ต้องสมบุกสมบัน ขึ้นลงทะเล สิ่งที่สวมได้ง่ายและสบายที่สุดคือ ผ้าขาวม้าสำหรับผู้ชาย และ เสื้อคอกระเช้า-ผ้าถุงสำหรับผู้หญิง ชาวมอแกลนต้องซื้อหาเสื้อผ้าจากภายนอก และการที่ชาวมอแกลนมี การปฏิสัมพันธ์กับคนหลายกลุ่มมาตั้งแต่อดีตการหยิบยืมและยอมรับวัฒนธรรมอื่น ทำให้เกิดการผสมผสาน ทางวัฒนธรรม โดยเฉพาะการแต่งกายท่ีเห็นได้ชัดเจน', 'E52'),
('CL0148', 'ชุดเชวา(ผู้หญิงกะเหรี่ยงที่ยังโสด)', 'จะแต่งด้วยผ้าทรงกระสอบสีขาว มีลวดลายแต่งตามตัวเล็กน้อย บริเวณหน้าอกมีลายแดงทำเป็นชายครุย', 'E06'),
('CL0149', 'ชุดเชโม่ซู(ผู้หญิงกะเหรี่ยงที่แต่งงานแล้ว)', 'จะใส่ผ้าถุงสีแดงมีลายขวางตามตัว ซิ่นเป็นเส้นสีดำ ฟ้า น้ำตาล ส่วนเสื้อท่อนบนจะแต่งตามเผ่า', 'E06'),
('CL0150', 'ชุดกะเหรี่ยง(ผู้ชาย)', 'เสื้อผู้ชายวัยหนุ่มใช้สีแดงทุกกลุ่ม แต่มีลวดลายมากน้อยต่างกัน ', 'E06'),
('CL0151', 'หญาดฮะเหริ่มโตะ หรือ หญาดฮะเมินโตะ', 'การปักผ้าสไบด้วยรูปแบบลวดลายดอกพิกุล และลายดอกจันทร์ล้อมรอบดอกพิกุล ที่ใช้ห่มยามเข้าวัดหรือในพิธีสำคัญ หญาดฮะเหริ่มโตะ ของทางสมุทรสาครใช้ได้ทั้งชายหญิงโดยเฉพาะในวัยหนุ่มสาว', 'E53'),
('CL0152', 'ชุดมอญ(ชาย)', 'สวม เกลิด  หมายถึงผ้านุ่ง ส่วนผ้าผืนยาวที่นุ่งเวลาออกงานสำคัญ เรียกว่าเกลิดฮะเหลิ่น แปลว่า ผ้านุ่งยาว(ลอยชาย) ส่วนเสื้อ เป็นเสื้อคอกลมผ่าอกตลอด แขนกระบอก มีกระดุมผ้า หรือเชือกผูกเข้ากัน สมัยก่อนนิยมโพกศีรษะ ต่อมาตัดสั้นแบบสมัยนิยม', 'E53'),
('CL0153', 'ชุดชาวอิ้วเมี่ยน(หญิง)', 'ประกอบด้วยกางเกงขาก๊วยซึ่งพราวไปด้วยลายปักที่จะแสดงฝีมือกันเต็มที่ โดยสวมกับเสื้อคลุมตัวยาว แต่จะรวบชายเสื้อด้านหน้าจากเอวไปไว้ด้านหลังเพื่อโชว์ลายปักที่กางเกง และที่สาบเสื้อด้านในรอบคอลงมาถึงเอวจะติดปุยไหมพรมสีแดงมีผ้าคาดเอวและผ้าโพกศีรษะ', 'E72'),
('CL0154', 'ชุดชาวอิ้วเมี่ยน(ชาย)', 'สวมเสื้อตัวสั้นกับกางเกงขาก๊วย มีการปักและประดับริมผ้าหรือปักลวดลายงดงามเป็นกระเป๋า สำหรับเครื่องประดับนั้นจะนิยมสวมเครื่องเงินเป็นกำไลคอและมีสร้อยระย้าห้อยลงด้านหลังฝีมือการทำเครื่องเงินของชาวเมี่ยนนั้นเป็นที่เลื่องลือในหมู่ชาวเผ่าว่างงดงามนัก', 'E72');
