INSERT INTO `ethnicdata` (`Ethnic_id`, `Ethnic_nameth`, `Ethnic_nameen`, `Ethnic_history`, `Ethnic_language`, `Ethnic_population`, `img`) VALUES
('E01', 'ก่อ(อึมปี้)', 'Ko Uem Pi', 'คนรุ่นปัจจุบันเชื่อว่าบรรพบุรุษของตนถูกกวาดต้อนจากสิบสองปันนาในมณฑลยูนนานของประเทศจีน ในยุค “เก็บผักใส่ซ้า เก็บข้าใส่เมือง” โดยเริ่มมาตั้งชุมชนที่บ้านม่วงเน่า ปัจจุบันคือบ้านเชียงแล อำเภอท่าวังผา จังหวัดน่าน จากนั้นได้แยกย้ายออกไปเป็นสองกลุ่ม กลุ่มแรกอยู่ที่บ้านเสือกึน (สะเกิน) ในพื้นที่ภูลังกา อำเภอสองแคว จังหวัดน่าน ทำหน้าที่ผลิตดินประสิว/ดินปืน (จากมูลค้างคาว) ให้เจ้าเมืองน่าน ขณะที่อีกกลุ่มหนึ่งถูกบังคับให้ไปเลี้ยงช้างและม้าให้กับเจ้าเมืองแพร่ ที่ตัวจังหวัดแพร่ ตอนแยกกันใหม่ๆ ทั้งสองกลุ่มยังเดินทางไปมาหาสู่กันอยู่ แต่ระยะหลังเริ่มเหินห่างกันออกไป กลุ่มที่อยู่สะเกิน (เขตจังหวัดน่าน) ยังเรียกตัวเองว่า “ก่อ” หรือ “ก๋อง” ขณะที่กลุ่มที่อยู่ใกล้ตัวเมืองแพร่เรียกตัวเองว่า “อึมปี้” เข้าใจว่าเพี้ยนมาจากคำว่า “แป้” (แพร่) ซึ่งเป็นคำบ่งบอกถึงอัตลักษณ์ของคนก่อที่เมืองแพร่', 'ตระกูลภาษาทิเบโต-เบอร์มัน (Tibeto-Burman) สายโลโลใต้ (Southern Lolo) กลุ่มย่อยฮาโนอิช (Hanoish)', '1500', 'E01.jpg'),
('E02', 'กะซอง', 'Ka Song', 'ชาวชองกระจายตัวอยู่ในบริเวณภาคตะวันออก บริเวณจังหวัดจันทบุรี ตราด ฉะเชิงเทรา โดยพบว่ามีความหนาแน่นบริเวณ ตำบลตะเคียนทอง ตำบลคลองพลู อำเภอเขาคิชฌกูฏ จังหวัดจันทบุรี ผู้ชายชาวชองสูงวัยยังคงนิยมนุ่งโสร่งและเคียนผ้าขาวม้า หญิงสูงวัยยังมีบางส่วนที่นุ่งโจงกระเบน ชาวชองในแถบน้ำดำรงชีพในวิถีเกษตร ทั้งทำนา ทำไร่และมีพืชผักประจำถิ่นอย่างหมาก กระวานและเร่ว ชาวชองมีความเชื่อผสมผสานกันระหว่างศาสนาผี พุทธ และพราหมณ์ โดยศาสนาผีเป็นศาสนาดั้งเดิมก่อนการรับศาสนาพุทธและพรามหณ์เข้ามา', 'ตระกูลออสโตรเอเชียติก ไม่มีภาษาเขียนเป็นของตนเอง แต่มีภาษากะซองเป็นภาษาพูด', '3000', 'E02.jpg'),
('E03', 'กะโผล่ว ', 'Ka Phlo', 'ชาวกะเหรี่ยงโปว์ กะโผล่ว หรือโผล่ว อาศัยอยู่ทางเหนือเรื่อยลงมาทางแถบเทือกเขาตะวันตก เช่น เชียงใหม่ ตาก กาญจนบุรี ราชบุรี เพชรบุรี จนถึงประจวบคีรีขันธ์ มีภาษาพูดเป็นของตนเอง ปัจจุบันในชุมชนกะเหรี่ยงโผล่ง ทั้งเด็กและผู้ใหญ่ยังใช้ภาษาของตน และมีเอกลักษณ์เครื่องแต่งกายที่ชัดเจน โดยเฉพาะลายเสื้อและผ้านุ่งที่ทอใช้เองในครัวเรือน', 'ตระกูลภาษาจีน-ธิเบต', '35000', 'E03.jpg'),
('E04', 'กะเลิง', 'Ka Leng', 'ชาวกะเลิงอาศัยกระจายตัวอยู่เป็นชุมชนในหลายจังหวัดของภาคอีสาน โดยเฉพาะในเขตจังหวัดมุกดาหารและจังหวัดสกลนคร  ชาวกะเลิง  บ้านดอนตาล อ. ดอนตาล จ. มุกดาหาร มีพิธีกรรมสำคัญคือพิธีเลี้ยงผีเจ้าปู่“เจ้าโฮงแป้นแผ่นทองเหลือง”ชาวบ้านเชื่อว่า เจ้าปู่ชอบสีแดง ด้วยเหตุนี้เมื่อถึงบุญพิธีเลี้ยงประจำปี ชาวบ้านจะนำเสื้อผ้าสีแดงพร้อมดอกไม้ ธูปเทียน เหล้าขาว หมูหรือไก่ อันเป็นเครื่องเซ่นไปถวายท่านและร่วมเฉลิมฉลองกินดื่มเพื่อให้เจ้าปู่พึงพอใจอันยังผลให้เกิดการปกป้องคุ้มครองดูแลลูกหลาน รวมถึงความอุดมสมบูรณ์ในการผลผลิต', 'ตระกูลออสโตรเอเชียติก', '70000', 'E04.jpg'),
('E05', 'กะหร่าง', 'Ka Rang', 'เป็นกลุ่มชาติพันธุ์หนึ่ง มีภาษาพูดเรียกว่าภาษากะเหรี่ยง จัดอยู่ในตระกูลภาษาจีน-ทิเบต อาศัยอยู่มากในรัฐกะเหรี่ยง ภาคใต้และภาคตะวันออกเฉียงใต้ของพม่า กะเหรี่ยงมีประชากรประมาณร้อยละ 7 ของประชากรชาวพม่าทั้งหมด ชาวกะเหรี่ยงจำนวนมากอพยพไปอยู่ในประเทศไทย ส่วนใหญ่อาศัยตามแนวชายแดนไทยพม่า อาศัยอยู่ใน 15 จังหวัด ของภาคเหนือและภาคตะวันตก ได้แก่ กาญจนบุรี กำแพงเพชร เชียงราย เชียงใหม่ ตากประจวบคีรีขันธ์ เพชรบุรี แพร่ น่าน แม่ฮ่องสอน ราชบุรี ลำปางลำพูน สุโขทัย สุพรรณบุรี และอุทัยธานี', 'ตระกูลภาษาจีน-ธิเบต', '350000', 'E05.jpeg'),
('E06', 'กะเหรี่ยง', 'Karen ', 'ชาวกะเหรี่ยง บ้านบางกลอย ต.ห้วยแม่แพรียง อ.แก่งกระจาน จ.เพชรบุรี  ต้องประสบกับปัญหาการโยกย้ายถื่นฐานจากพื้นที่ทำกินเดิมในเขตต้นน้ำ หรือบ้านใจแผ่นดิน  ออกมายังพื้นที่ใหม่ซึ่งรัฐจัดสรรให้ แต่พบว่าสภาพพื้นที่ไม่เหมาะสมต่อการเกษตร วิถีชีวิตของพวกเขาเปลี่ยนไปเมื่อไม่สามารถทำเกษตรได้ผลผลิตที่เพียงพอต่อการบริโภคในครัวเรือน ความอดอยาก ขาดแคลนอาหาร การต้องใช้เงินจำนวนหนึ่งในการดำรงชีวิตประจำวันแตกต่างกับบ้านเดิมที่หาอยู่หากินจากทรัพยากรธรรมชาติ ผลิตข้าวได้เพียงพอต่อการบริโภค  ทำให้หลายครอบครัวตัดสินใจย้ายกลับหมู่บ้านเดิมในป่าใหญ่ แต่ต้องถูกบังคับย้ายเช่นเดิม ซ้ำวนไป', 'ตระกูล ทิเบต-พม่า', '1280', 'E06.jpg'),
('E07', 'กะเหรี่ยงคอยาว', 'Longneck Karen', 'ชาวกะยันหรือกะเหรี่ยงคอยาว เป็นกลุ่มชาติพันธุ์ที่มีถิ่นอาศัยอยู่ในประเทศพม่า ที่ได้รับผล<br>กระทบจากการสู้รบระหว่างกองกำลังกะเหรี่ยงกับรัฐบาลทหารพม่าได้อพยพหนีภัยสงครามเข้ามาอาศัยตามตะเข็บแนวชายแดน และบางส่วนได้เข้ามาอาศัยในเขตประเทศไทย ในพื้นที่จังหวัดแม่ฮ่องสอน โดยปัจจุบันเมื่อลี้ภัยเข้ามาอยู่ในประเทศไทยนั้นวิถีชีวิต วัฒนธรรมและประเพณีจะเปลี่ยนไปจากเดิม จากที่เคยอาศัยอยู่ในประเทศพม่า ทำไร่ ทำนา เลี้ยงสัตว์ และหาของป่า เมื่อมาอยู่ในประเทศไทยจะไม่มีที่ดินทำกินเป็นของตนเอง จึงปรับเปลี่ยนไปเป็นการทอผ้าและขายของ', 'ตระกูล ทิเบต-พม่า', '300', 'E07.jpg'),
('E08', 'กะเหรี่ยงแดง', 'Bar Karen / Bwa', 'กะเหรี่ยงแดง กะเหรี่ยงแบร หรือ บเว นักวิชาการอนุมานว่าได้เริ่มอพยพเคลื่อนย้ายจากประเทศพม่าเข้ามาสู่ประเทศไทยหลังยุคล่าอาณานิคม และมีความเข้มข้นของการเคลื่อนย้ายเมื่อเกิดสถานการณ์สู้รบในประเทศพม่าหลังการปลอดปล่อยพม่าจากการเป็นอาณานิคมของประเทศอังกฤษ สงครามระหว่างกลุ่มชาติพันธุ์กับรัฐบาลทหารพม่า สถานการณ์ความไม่สงบภายในประเทศผนวกกับความยากแค้นในการดำรงชีพ ทั้งการขูดรีดผลผลิตและแรงงาน การเกณฑ์ใช้แรงงานและการบังคับเคลื่อนย้ายชุมชนของรัฐบาลทหารพม่า รวมถึงแนวนโยบายด้านการท่องเที่ยงของประเทศไทยที่เป็นแรงดึงดูดให้มีการนำเข้ากลุ่มชาติพันธุ์กะเหรี่ยงกลุ่มย่อยอื่นตามมา เช่น กลุ่มกะยา ปะโอ  ตองสู กะยัน กะยอ ', 'ตระกูลภาษาจีน-ธิเบต', '600', 'E08.jpg'),
('E09', 'กะเหรี่ยงโพล่ง', 'Karen Pwo ', 'กะเหรี่ยงโพล่ง มีทั้งส่วนที่นับถือศาสนาคริสต์ นับถือผีบรรพบุรุษและส่วนมากนับถือศาสนาพุทธ โดยกะเหรี่ยงพุทธนั้นมีพิธีกรรมสำคัญในทางศาสนาได้แก่ งานออกพรรษา ทำบุญถวายเทพสิ่งศักดิ์สิทธิ์ ประเพณีถวายราหุล \"กวนยาฮู\" ประเพณีการไหว้เจดีย์ทราย ประเพณีวันสงกรานต์ ไหว้ต้นโพธิ์ ค้ำโพธิ์ ประเพณีการสร้างสะพานหรือบ่งโถ่ว โดยประเพณีสำคัญคือการทำขวัญควายเพื่อเป็นการขอบคุณสัตว์เลี้ยงที่ช่วยทำงานในไร่นา ', 'ตระกูลภาษาจีน-ธิเบต', '100000', 'E09.jpg'),
('E10', 'กูย', 'Kui', '“กูย” หรือ “กวย” เป็นคำเรียกชื่อตัวเองของกลุ่มชาติพันธุ์หนึ่งที่มีอัตลักษณ์ด้านภาษา วัฒนธรรม และวิถีชีวิต อาศัยอยู่อาณาบริเวณลุ่มน้ำโขงและเทือกเขาพนมดงรัก นักภาษาศาสตร์จัดภาษากูยให้อยู่ในตระกูลออสโตรเอเชียติก สาขามอญเขมร กลุ่มเวียดกะตู ชนกลุ่มนี้มีจำนวนหนาแน่นที่สุดในจังหวัดสุรินทร์ ศรีสะเกษและบุรีรัมย์ บางส่วนอาศัยอยู่ในจังหวัดร้อยเอ็ด มหาสารคาม ปราจีนบุรี และอุบลราชธานี นอกจากนี้ยังพบว่า มีคนพูดภาษากูยตั้งถิ่นฐานกระจายตัวอย่างประปรายในพื้นที่ภาคกลางและภาคตะวันออกของไทยอีกด้วย ', 'ตระกูลออสโตรเอเชียติก', '400000', 'E10.jpg'),
('E11', 'ขมุ', 'Khamu', 'ปัจจุบันคนขมุไทยเหล่านี้นับว่ามีความสำคัญต่อสังคมไทยในแง่ของการเป็นแรงงานในภาคการเกษตร พวกเขามีวิถีการดำรงชีพ อัตลักษณ์ทางด้านประเพณีวัฒนธรรม ระบบความเชื่อที่สะท้อนถึงตัวตนของเขา นอกจากนี้แล้วคนขมุไทยนับว่ามีการสร้างพื้นที่ทางสังคมและวัฒนธรรมผ่านการสร้างกลุ่มเครือข่ายที่สัมพันธ์ไปด้วยดีกับชนพื้นราบ เช่น กลุ่มคนไทลื้อ กลุ่มชาติพันธุ์ต่างๆ เพื่อให้ฐานะของคนขมุทัดเทียมกับชนพื้นเมืองหรือการเป็น “พลเมืองไทย” ที่มีความสมบูรณ์และมีความชัดเจนมากยิ่งขึ้นท่ามกลางความหลากหลายของกลุ่มชาติพันธุ์ในไทย', 'ตระกูลออสโตรเอเชียติก สาขามอญ-เขมร ภาษาพูด: ขมุ ภาษาคำเมือง ภาษาไทย (ชาวขมุจะมีภาษาพูดเป็นของตนเองตามสำเนียงเสียงท้องถิ่นและจะแตกต่างกันไปในแต่ละกลุ่ม ส่วนใหญ่แล้วจะสามารถสื่อสารกันได้อย่างเข้าใจเพราะมีการเรียนรู้ทางภาษาระหว่างกัน)', '12000', 'E11.jpg'),
('E12', 'ขแมร์ลือ', 'Khmea Lue', 'กลุ่มชาติพันธุ์ขแมร์ลือหรือคนเขมรถิ่นไทยที่อาศัยอยู่ในประเทศไทย ได้นิยามตนเองให้แตกต่างจากคนกลุ่มชาติพันธุ์เดียวกันที่อาศัยอยู่ในประเทศกัมพูชา ด้วยการเรียกตนเองว่า \"ขแมร์-ลือ\" ที่สื่อความหมายถึง ชาวเขมรที่อยู่ทางตอนเหนือของประเทศกัมพูชา และเรียกชาวเขมรในประเทศกัมพูชาว่า \"ขแมร์-กรอม\" อันหมายถึง เขมรต่ำหรือ กลุ่มคนเขมรที่อาศัยอยู่ในประเทศกัมพูชา ', 'ตระกูลออสโตรเอเซียติก', '1.4 ล้าน', 'E12.jpeg'),
('E13', 'คนใต้', 'Southerners', 'ภาคใต้ของไทยมีเนื้อที่รวม 70,715.2 ตารางกิโลเมตร ตั้งอยู่บนคาบสมุทรมลายู ขนาบด้วยทะเลทั้ง 2 ด้าน ประกอบด้วยอ่าวไทยทางฝั่งตะวันออก และทะเลอันดามันทางฝั่งตะวันตกความยาวจากเหนือจรดใต้ประมาณ 750 กิโลเมตร ประกอบไปด้วย 14 จังหวัด คือ ชุมพร สุราษฎ์ธานี นครศรีธรรมราช พัทลุง สงขลา ตรัง ระนอง พังงา กระบี่ ภูเก็ต สะตูล ปัตตานี นราธิวาส ยะลา อัตลักษณ์ ใน 14 จังหวัดของภาคใต้มีความหลากหลาย ทั้งในชาติพันธุ์ (เชื้อสายมาลายู, เชื้อสายจีน) และการนับถือศาสนา (พุทธ, อิสลาม)  ลักษณะภูมิประเทศภาคใต้เป็นคาบสมุทรที่มีทะเลขนาบอยู่ 2 ด้าน คือ ตะวันออกติดทะเลจีนใต้ด้านอ่าวไทย ตะวันตกติดทะเลอันดามันและมีแนวเทือกเขาอยู่ในบริเวณตอนกลางของภูมิภาค', 'ภาษาไทยสำเนียงใต้ แตกต่างกันในแต่ละท้องถิ่นใต้ ในขณะที่ 5 จังหวัดที่มีชายแดนติดกับมาเลเซียจะมีภาษาพูดและตัวเขียนที่เรียกว่า ภาษายาวี รวมถึงมีการใช้ภาษามาเลย์ในชีวิตประจำวัน', '4.5 ล้าน', 'E13.jpg'),
('E14', 'จีนฝูโจว', 'Fujo Chinese', 'คนจีน ในประเทศไทยมักมีหลากหลายกลุ่มด้วยกัน ดังนั้นคนไทยจึงนิยมเรียกคนจีนด้วยชื่อกลุ่มต่อท้าย เพื่อเป็นการจำแนกอีกทีซึ่งมีดังนี้ “จีนฝูโจว” “จีนฮกจิว” “จีนแคะ” “จีนไหหลำ” “จีนกวางตุ้ง” “จีนแต้จิ้ว” เป็นต้น โดยจีนฟุโจวก็เป็นอีกหนึ่งชาติพันธุ์ที่มีอัตลักษณ์เป็นของตัวเอง', 'ตระกูลภาษาจีน-ธิเบต', '100000', 'E14.jpg'),
('E15', 'จีนยูนนาน', 'Yunnan Chinese', '\"จีนฮ่อ\" จึงเป็นชื่อเรียกชาวจีนใต้ของคนไทยเหนือ โดยชื่อนี้ปรากฏขึ้นมาตั้งแต่สมัยประวัติศาสตร์ของอาณาจักรล้านนา ล้านช้างที่เคยติดต่อสัมพันธ์และทำสงครามกันในอดีต เช่นเดียวกัน ชื่อฮ่อ ยังถูกใช้เรียกกลุ่มกองคาราวานค้าขายและกลุ่มอพยพอพยพจากตอนใต้ของจีนที่เข้ามาสู่ทางภาคเหนือของไทยจนถึงปัจจุบัน', 'ตระกูลภาษาจีน-ธิเบต', '200000', 'E15.jpg'),
('E16', 'ซะโอจ', 'SaOoj', 'แต่เดิมชาวซะโอจตั้งถิ่นฐานอยู่ห่างจากลำแม่น้ำแควใหญ่ประมาณ 5 กิโลเมตร ต่อมา  ปี พ.ศ. 2523 รัฐบาลไทยมีความพยายามจะสร้างเขื่อนศรีนครินทร์ ส่งผลให้ชุมชนเดิมที่เคยอยู่อีกฝั่งหนึ่งของแม่น้ำแควใหญ่ถูกน้ำท่วม อันเป็นผลกระทบมาจากการสร้างอ่างเก็บน้ำทางการไฟฟ้าฝ่ายผลิตเขื่อนศรีนครินทร์ได้ทำการกักเก็บน้ำเพื่อใช้ในการผลิตกระแสไฟฟ้า จึงได้ทำการอพยพมาตั้งอยู่ที่ หมู่บ้านหนองหว้า', 'ตระกูลภาษาออสโตรเอเชียติก กลุ่มภาษาเปียริก ไม่มีภาษาเขียน', '50', 'E16.jpeg'),
('E17', 'ซำเร', 'Sum Re', 'ชาวซำเรที่บ้านคลองโอน ต.นนทรี อ.บ่อไร่ จ.ตราด ยังดำรงชีพกับวิถีเกษตรกรรม ทำไร่ทำสวน รวมถึงเก็บหาของป่า ผัก เห็ด ล่าสัตว์ขนาดเล็ก ในแถบเทือกเขาบริเวณใกล้เคียงชุมชน ทั้งเพื่อเป็นอาหารและเป็นรายได้เสริม ผลผลิตที่สำคัญของชุมชนคือผลิตภัณฑ์เครื่องจักสานจากคลุ้มและไม้ไผ่ของ “กลุ่มวิสาหกิจชุมชนจักสานบ้านคลองโอน” เป็นกิจกรรมที่รวมกลุ่มเพื่ออนุรักษ์ภูมิปัญญาชาวบ้านให้อยู่คู่ชุมชนตลอดไป ', 'ตระกูลภาษาออสโตรเอเชียติก (Austro-Asiatic) สาขามอญ-เขมร', '1000', 'E17.jpeg'),
('E18', 'โซ่ (ทะวืง)', 'Zo', 'เรื่องราวความเป็นมาของกลุ่มชาติพันธุ์โซ่ (ทะวืง) ไม่ค่อยปรากฏหลักฐานมากนัก หลักฐานที่ได้ส่วนมากมาจากประวัติศาสตร์จากคำบอกเล่าของผู้เฒ่าผู้แก่มีข้อมูลค่อนข้างตรงกันว่า อพยพเข้ามาประประเทศไทยในรัชสมัยพระบาทสมเด็จพระนั่งเกล้าเจ้าอยู่หัว  รัชกาลที่ 3 โดยถูกกวาดต้อนหรือการเกลี้ยกล่อมมาพร้อมกับกลุ่มคนอื่น ๆ เช่น ชาวลาว ชาวผู้ไท ชาวญ้อ ในสมัยนั้น แล้วมาตั้งหลักปักฐานบ้านเรือนอยู่ในหลายจังหวัด ได้แก่ นครพนม สกลนคร มุกดาหาร และกาฬสินธุ์', 'ภาษาโซ่ (ทะวืง) เป็นภาษาของกลุ่มชาติพันธุ์หนึ่งในประเทศไทย จัดอยู่ในสาขาเวียดติกในกลุ่มมอญ-เขมร ในตระกูลภาษาออสโตรเอเชียติก', '1500', 'E18.jpeg'),
('E19', 'ญ้อ', 'Yo', 'ญ้อ  เป็นกลุ่มชาติพันธุ์อีกกลุ่มในพื้นที่ภาคอีสาน  ส่วนใหญ่อยู่ในพื้นที่อีสานตอนกลางถึงอีสานตอนเหนือ เช่น นครพนม กาฬสินธุ์ มหาสารคาม  โดยเฉพาะที่บ้านท่าขอนยาง จังหวัดมหาสารคามนั้น ถือเป็นชุมชนชาวญ้อขนาดใหญ่ ที่ยังสืบทอดประเพณี วัฒนธรรมมายาวนานไทยมุสลิม', 'ตระกูลพูดภาษาไท-กะได ส่วนของภาษาพูด : ภาษาญ้อ ภาษาลาวอีสาน ภาษาไทย และภาษาเขียน : ภาษาไทย', '400000', 'E19.jpg'),
('E20', 'ญัฮกุร', 'Nyah Kur', 'ชาวญัฮกุรกระจายตัวอยู่ในแนวเทือกเขาพังเหย อ.เทพสถิตย์ จ.ชัยภูมิ วิถีชีวิตปรับเปลี่ยนเข้าสู่ระบบเกษตรแผนใหม่ บ้านเรือนมีความมั่นคงขึ้น บริเวณองค์การบริหารส่วนตำบลบ้านไร่มีการสร้างบ้านจำลองเป็นบ้านแบบดั้งเดิมของชาวญัฮกุรในพื้นที่ ด้านการแต่งกายในชีวิตประจำวันจะใส่เสื้อผ้าตามสมัยนิยม แต่เมื่อมีกิจกรรมทางสังคมหรือพิธีกรรมจะสวมใส่เสื้อผ้าตามอัตลักษณ์ชาติพันธุ์ ', 'ภาษาออสโตรเอเชียติก (Austro-Asiatic language family) ในสาขาโมนิก (Monic) หรือมอญ จัดอยู่ในกลุ่มภาษามอญโบราณ (Old Mon)', '6000', 'E20.jpg'),
('E21', 'เญอ', 'Yer', ' ในมุมมองของนักภาษาศาสตร์และนักมนุษยศาสตร์นั้น \"ชาวเยอ\" เป็นกลุ่มชาติพันธุ์หนึ่งของ  ชาวกวย ที่อยู่ในกลุ่มภาษาตระกูลมอญข-เขมร โดยมีหลักฐานทางประวัติศาสตร์ของการอพยพย้ายถิ่นมาอย่างยาวนาน แต่ด้วยหลักฐานทางประวัติศาสตร์เกี่ยวกับชาวเยอมีอยู่อย่างจำกัด อีกทั้งพื้นที่อยู่อาศัย ลักษณะทางกายภาพของผู้คน และความสัมพันธ์ของชาวเยอมีความสัมพันธ์ใกล้ชิดกับชาวกวย', 'ไม่มีภาษาเขียน ส่วนภาษาพูด ภาษาเยอ อยู่ในตระกูลออสโตรเอเชียติก สาขามอญ-เขมร', '15000', 'E21.jpeg'),
('E22', 'ดาราอาง', 'Daraang', 'ชาวดาราอางบริเวณตะเข็บชายแดนไทย-เมียนมาร์ แถบอำเภอเชียงดาว อำเภอฝาง จังหวัดเชียงใหม่ ดำรงชีพด้วยระบบเกษตรบนพื้นที่สูง โดยเฉพาะการทำไร่สตรอเบอร์รี่ส่งโครงการหลวง มีวิถีชีวิต ความเป็นอยู่ที่เรียบง่าย เคารพธรรมชาติ ศรัทธาในพระพุทธศาสนา ผู้หญิงยังมีบทบาทหลักในการผลิตสินค้าของที่ระลึกสำหรับขายให้นักท่องเที่ยว', 'ตระกูลออสโตรเอเชียติก สาขามอญ-เขมร', '2000', 'E22.jpg'),
('E23', 'ไต', 'Tai', 'กลุ่มชาติพันธุ์ \"ไต\" อาศัยอยู่ตั้งแต่ตอนเหนือเวียดนาม ตลอดแนวตอนใต้ของจีน ลาว ไทย ทางตอนเหนือของพม่าไปจนถึงแคว้นอัสสัมของอินเดีย ด้วยการกระจายตัวไปในพื้นที่ที่แตกต่าง พวกเขาจึงมีชื่อเรียกมากมายตามประเทศและกลุ่มคนที่อาศัยร่วม ', 'ตระกูลไท-กะได', '90000', 'E23.jpg'),
('E24', 'ไตหย่า', 'Tai Ya', 'ชาวไตหย่าอาศัยอยู่ในพื้นที่ อำเภอเมือง จังหวัดเชียงราย โดยบรรพบุรุษเดินทางมาประเทศไทยในฐานะลูกหาบพร้อมกับคณะมิชชันนารีที่เดินทางจากประเทศจีนเข้าสู่ประเทศไทย มีอัตลักษณ์โดดเด่นคือเครื่องแต่งกาย ผู้หญิงชาวไตหย่าจะสวมผ้าซิ่น 2 ผืนซ้อนกัน ผืนแรกเรียกว่า ผ้าไต่เซิน (ผ้าถุงที่ใส่ชั้นในสุด) เป็นผ้าพื้นสีดำประดับด้วยริ้วผ้าสีต่างๆ เย็บเป็นแถบชายซิ่น ส่วนผ้าผืนที่สอง เรียกว่าผ้าเซิน (ผ้าถุง) เป็นผ้าพื้นสีดำประดับชายผ้าด้วยริ้วผ้าสีแต่ไม่เย็บด้านข้างให้ติดกัน ขณะสวมใส่จะรั้งผ้าซิ่นด้านซ้ายขึ้น ปล่อยชายด้านขาวห้อยต่ำลงมา แล้วคาดเข็มขัดที่แต่งด้วยเม็ดเงินมีปลายเป็นพวงพู่ห้อยสวยงาม', 'ตระกูลไท-กะได ที่จัดอยู่ในกลุ่มสายตะวันตกเฉียงใต้', '300', 'E24.jpg'),
('E25', 'ไทขึน', 'Thai Khuen', 'กลุ่มชาติพันธุ์ไทขึนหรือไทเขิน ถูกกวาดต้อนมาตั้งถิ่นฐานที่แรกบริเวณประตูหายยา จ.เชียงใหม่ กระจายอาศัยกลืนกลายเป็นส่วนหนึ่งของรัฐจารีตล้านนา มีกลุ่มสินค้าหัตถกรรมที่เรียกว่า เครื่องเขิน และคำว่า เขิน นี้กลายเป็นชื่อเรียกอีกชื่อหนึ่ง และกลายเป็นเครื่องมือที่ใช้ในการรื้อฟื้นเพื่อสร้างตัวตนทางชาติพันธุ์ ชาวไทขึน ไตขึน หรือไตเขิน เป็นกลุ่มชาติพันธุ์มี่ทีจำนวนไม่มากนัก อีกทั้งเริ่มกลืนกลายทางวัฒนธรรมกับคนไทยวนท้องถิ่นของภาคเหนือ  อัตลักษณ์สำคัญของชาวไทขึนคือการผลิตเครืองเขิน  โดยเฉพาะเครื่องเขินแบบลายขูด (ฮายลาย) เป็นลักษณะเฉพาะที่ทำขึ้นในพื้นที่ชุมชนวัดนันทาราม อย่างไรก็ตาม ชาวไทเขินได้เลิกทำเครื่องเขินในช่วงทศวรรษ 2520 โดยความรู้เหล่านี้ถูกถ่ายเทและสืบทอดสู่คนท้องถิ่นกลุ่มอื่น ๆ ', 'ตระกูลภาษาไท-กะได กลุ่มภาษา คํา-ไต กลุ่มภาษาย่อยไทพายัพ ชาวไทขึนใช้อักษรชนิดเดียวกันกับอักษรล้านนา (ตัวเมือง)', '20000', 'E25.jpg'),
('E26', 'ไทยโคราช', 'Thai Korat', 'คนไทยโคราช ไม่ใช่ชื่อเรียกชนชาติและเชื้อชาติ ไม่ใช่อัตลักษณ์ชาติพันธุ์ที่ตายตัว แต่เป็นกระบวนการสร้างอัตลักษณ์ทางชาติพันธุ์ที่ผ่านการกรองและต่อรองกับอำนาจของรัฐศูนย์กลางของคนกลุ่มวัฒนธรรมไทย จนตกผลึกเป็น \"ไทยโคราช\" อัตลักษณ์ที่มีความคล้ายไปทางวัฒนธรรมไทยภาคกลางมากกว่าวัฒนธรรมลาวในอีสาน', 'ตระกูลภาษาไทย-ลาว สำเนียงโคราช มีรากเหง้าจากสำเนียงหลวงกรุงศรีอยุธยาของคนยุคต้นอยุธยาที่พูดเหน่อ(แบบลาว) มีสำเนียงต่างจากคนไทย แต่มีความใกล้เคียงสำเนียงระยองและจันทบุรีทางชายทะเลฝั่งตะวันออกเพราะมีรากเหง้าจากสองฝั่งโขงด้วยกัน', '650000', 'E26.jpg'),
('E27', 'ไทยเชื้อสายจีน', 'Chinese', 'ตึ่งนั้ง หรือ คนจีน มาจากชาวจีนดินแดนต่าง ๆ ในเอเชียตะวันออกเฉียงใต้ ที่ปรากฏขึ้นในสมัยราชวงศ์“ถั่ง” คนจีนโพ้นทะเลจึงเรียกตนเองว่า “ตึ่งนั้ง” ซึ่งแปลว่า “ชาวถัง” ในสําเนียงภาษาจีนแต้จิ๋ว  ในปัจจุบันชาวไทยเชื้อสายจีน มีประมาณ 10 ล้านคนในประเทศไทย คิดเป็น 11–14% ของจำนวนประชากรทั้งหมดของประเทศ และยังมีอีกจำนวนมากไม่สามารถนับได้ เพราะที่กลมกลืนกับคนไทยไปแล้วโดยการแต่งงานข้ามเชื้อชาติ ชาวจีนที่อพยพมานั้น มักมาจากดินแดนทางตอนใต้ของประเทศจีน คือ มณฑลกวางตุ้ง ฮกเกี้ยน และไหหลำ ชาวจีนเหล่านี้ได้แก่ ชาวจีนกวางตุ้ง ชาวจีนแคะ ชาวจีนแต้จิ๋ว ชาวจีนฮกเกี้ยน และชาวจีนไหหลำ รวม 5 กลุ่มภาษาใหญ่ หลักฐานที่ชี้ให้เห็นถึงการอพยพเข้ามาตั้งถิ่นฐานของชาวจีน 5 กลุ่มภาษาดังกล่าวในประเทศไทย เห็นได้จากศาลเจ้า สมาคมตระกูลแซ่ สมาคมกลุ่มภาษา สมาคมถิ่นเกิด สมาคมอาชีพ และโรงเรียนจีน สาเหตุของการอพยพของชาวจีนเข้าสู่ไทยก็เพราะความไม่สงบภายในประเทศจีน ความอดอยาก การติดต่อค้าขาย และความสามารถในการเดินเรือของจีนออกสู่ทะเล ', 'ตระกูลภาษาจีน-ทิเบต ใช้ภาษาจีนกลางในการสื่อสาร ทั้งภาษาพูดและภาษาเขียน มีภาษาพูดที่แตกต่างออกไปตามชนกลุ่มย่อย เช่น ชาวจีนกวางตุ้ง ชาวจีนแต้จิ๋ว ชาวจีนฮกเกี้ยน ต่างมีสำเนียงพูดเป็นของคนเอง', '10 ล้าน', 'E27.jpg'),
('E28', 'ไทยทรงดำ', 'Thai Song Dam ', 'ลาวโซ่ง/ลาวไทยดำมีถิ่นฐานดั้งเดิมอยู่บริเวณภาคเหนือของประเทศเวียดนาม เคลื่อนย้ายเข้ามาสู่ประเทศไทยตั้งแต่ พ.ศ. 2322 ร่วมกับบรรดาชาวลาวกลุ่มต่าง ๆ ตั้งถิ่นฐานครั้งแรกที่จังหวัดเพชรบุรี หลังจากนั้นจึงขยายออกไป ซึ่งวัฒนธรรมของกลุ่มชาติพันธุ์ลาวโซ่งค่อนข้างเข้มแข็ง ยังคงรักษาไว้ซึ่งอัตลักษณ์ทางชาติพันธุ์ ลาวโซ่งในพื้นที่จังหวัดสมุทรสาคร ยังคงสืบทอดพิธีกรรมสำคัญ เช่น พิธีเสนแก้เคราะห์  พิธีเฮ็ดแฮว (หลังงานเผาศพ)  ประเพณีอิ้นกอน ฟ้อนแคน  พิธีเสนเรือน  โดยยังมีการสืบทอดของผู้นำหน้าที่ประกอบพิธีกรรมอย่างต่อเนื่อง ', 'ตระกูลภาษาไท-กะได ', '120000', 'E28.jpg'),
('E29', 'ไทยเบิ้ง', 'Thai Bueng', 'ไทยเบิ้ง ไทยเดิ้ง หรือไทยโคราช เป็นกลุ่มชนท้องถิ่นหนึ่งที่ตั้งบ้านเรือนอยู่บริเวณลุ่มแม่น้ำป่าสัก และยังตั้งถิ่นฐานกระจัดกระจายอยู่ในจังหวัดอื่น ๆ ได้แก่ ลพบุรี สระบุรี เพชรบูรณ์ บุรีรัมย์ ชัยภูมิ และนครราชสีมา ชาวไทยเบิ้งนั้นถูกนิยามว่าเป็นกลุ่มคนกลุ่มเดียวกับชาวไทยโคราช เพราะมีวัฒนธรรมและสำเนียงภาษาบางอย่างคล้ายกัน เนื่องจากมีความใกล้เคียงกันในด้านภูมิศาสตร์คือ กลุ่มชาวไทยเบิ้งมักจะอาศัยอยู่บริเวณรอยต่อระหว่างภาคกลางกับภาคตะวันออกเฉียงเหนือ', 'ตระกูลภาษาไทย-ลาว', '650000', 'E29.jpg'),
('E30', 'ไทยพลัดถิ่น', 'Thai-Cambodia', 'ชื่อ \"คนไทยเกาะกง\" เป็นชื่อที่คนเหล่านี้ใช้เรียกตัวเองเสมอไม่ว่าพวกเขาจะอยู่ที่อำเภอคลองใหญ่หรืออยู่ที่เมืองเกาะกงก็ตาม คำว่า \"ไทย\" คือ ชื่อกลุ่มชาติพันธุ์ ในขณะที่คำว่า \"เกาะกง\" คือ ชื่อของถิ่นกำเนิดและถิ่นที่พักอาศัยของพวกเขา คำว่า \"ไทยเกาะกง\" จึงเป็นนามที่บอกได้ทั้งกลุ่มชาติพันธุ์และถิ่นกำเนิด ถิ่นที่มาของกลุ่มชนเหล่านี้ได้โดยชัดเจน ในอดีตคนเหล่านี้ไม่ได้ถูกเรียกและเรียกตัวเองว่า \"คนไทยเกาะกง\" แต่พวกเขาคือ \"เสียมเกาะกง\" หรือ \"สยามเกาะกง\" นั่นเอง ไม่ว่าจะเป็นเสียมเกาะกงหรือไทยเกาะกง คนเหล่านี้ก็ยังนิยามตนเองเช่นนั้นเรื่อยมาจนถึงปัจจุบัน', 'ตระกูลภาษาไท-กะได (Tai-Kadai) ', '34930', 'E30.jpg'),
('E31', 'ไทยภาคกลาง', 'Thai', 'ไทยภาคกลาง เป็นชื่อที่เรียกตัวเองสำหรับคนไทยที่อาศัยอยู่ในบริเวณพื้นที่ภาคกลางของประเทศไทย โดยพื้นที่ภาคกลางของประเทศไทยส่วนใหญ่เป็นที่ราบลุ่มแม่น้ำที่อุดมสมบูรณ์  ส่งผลให้ผู้คนจากภูมิภาคต่าง ๆ อพยพเข้ามาทำมาหากินตั้งแต่อดีตจนถึงปัจจุบัน ไทยภาคกลาง เป็นชื่อที่เรียกตัวเองสำหรับคนไทยที่อาศัยอยู่ในบริเวณพื้นที่ภาคกลางของประเทศไทย โดยพื้นที่ภาคกลางของประเทศไทยส่วนใหญ่เป็นที่ราบลุ่มแม่น้ำที่อุดมสมบูรณ์  ส่งผลให้ผู้คนจากภูมิภาคต่าง ๆ อพยพเข้ามาทำมาหากินตั้งแต่อดีตจนถึงปัจจุบัน', 'ตระกูลภาษาไท-กะได (Tai-Kadai) ภาษาพูด: ภาษาไทยสำเนียงเหน่อตามท้องถิ่น และภาษาเขียน: อักษรไทย', '20 ล้าน', 'E31.jpg'),
('E32', 'ไทยมุสลิม', 'Thai Muslim', 'ไทยมุสลิม เป็นชื่อที่ใช้เรียกตัวเองของชาติพันธุ์นี้ คำว่า มุสลิม เป็นคำที่ใช้เรียกผู้ที่นับถือศาสนาอิสลาม หมายถึง ผู้ที่นอบน้อมต่อพระองค์อัลลอฮฺ (ซุบฮาฯ) แต่ผู้เดียวอย่างสิ้นเชิง เพื่อความสันติ ดังนั้นคำว่า ไทยมุสลิม จึงหมายถึง ประชากรในประเทศไทยหรือผู้ที่มีสัญชาติไทยที่นับถือศาสนาอิสลามและปฏิบัติตามหลักของศาสนา', 'ภาษามลายู หรือเรียกอีกอย่างว่าภาษามาเลย์ (Bahasa Melayu บาฮาซา มลายู) เป็นภาษาตระกูลออสโตรนีเซียน', '50000', 'E32.jpg'),
('E33', 'ไทยวน', 'Thai Yuan', 'ชาติพันธุ์ไทยวนเป็นกลุ่มคนที่มีประวัติศาสตร์ทางการเมืองและวัฒนธรรมในพื้นที่ภาคเหนือของไทยมาอย่างยาวนาน โดยมีรากบรรพบุรุษจากเมืองเชียงแสนหรืออาณาจักรโยนก ชื่อโยนกจึงเพี้ยนมาเป็นชื่อชาติพันธุ์ “ยวนหรือโยน”กระทั่งอาณาจักรโยนกได้ล่มสลายจากภัยธรรมชาติ คนเหล่านี้ได้เคลื่อนย้ายมาสร้างเมืองใหม่ คือ “เชียงใหม่” และเรียกขอบเขตของอำนาจแห่งนี้ว่าอาณาจักรล้านนา', 'ภาษาที่ใช้พูด : คำเมือง ตัวอักษรที่ใช้เขียน : ตัวอักษรธรรมล้านนาและอักษรฝักขาม', '6 ล้าน', 'E33.jpg'),
('E34', 'ไทโย้ย', 'Thai Yoei', 'ปัจจุบันชุมชนโย้ยในหลายพื้นที่ทั้งชุมชนเล็ก ชุมชนใหญ่ต่างๆมีความพยายามในการที่จะสืบสานและฟื้นฟูรากเหง้าทางประวัติศาสตร์และสร้างอัตลักษณ์ของตัวเองขึ้นมาใหม่ โดยมีการนำเสนอวัฒนธรรมผ่านวิธีการและรูปแบบต่างๆ ทั้งการละเล่น ความเชื่อ ประเพณี พิธีกรรมต่างๆ ดังเช่น ชุมชนโย้ยที่อากาศอำนวยได้มีการจัด “วันไทโย้ย” ขึ้นเป็นประจำทุกปี โดยตรงกับวันขึ้น 6 ค่ำ เดือน 6 เพื่อรำลึกถึงบรรพบุรษ เฉลิมฉลองชาติพันธุ์ของตนเองและเผยแพร่อัตลักษณ์ของชาวไทโย้ยต่อสาธารณะ', 'ตระกูลภาษา ไท-กะได ', '50000', 'E34.jpg'),
('E35', 'ไทโย้ย ', 'Thai Yo', 'กลุ่มชาติพันธุ์โย้ยนั้นอพยพมาจากฝั่งลาว อาศัยอยู่หนาแน่นในเขตพื้นที่จังหวัดสกลนคร อำเภออากาศอำนวย, วานรนิวาส, พังโคนและสว่างแดนดิน ปัจจุบันชุมชนโย้ยในหลายพื้นที่ต่างพยายามสืบสานและฟื้นฟูรากเหง้า สร้างอัตลักษณ์ของตนเองเพื่อความภาคภูมิใจและรองรับการท่องเที่ยวอีกด้วย', 'ตระกูลภาษา ไท-กะได (Tai-Kadai Language Family) ภาษาพูด ภาษาโย้ย ภาษาลาว/ไทอีสาน ภาษาไทย และตัวอักษรที่ใช้เขียน ภาษาไทย', '50000', 'E35.jpg'),
('E36', 'ไทลื้อ', 'Thai Lue', 'ไทลื้อ กลุ่มชนภาษาตระกูลไท อาศัยใช้ชีวิตตั้งถิ่นฐานมาอย่างยาวนานในเขตลุ่มน้ำโขงตอนบนหลายศตวรรษ นับถือทั้งความเชื่อดั้งเดิมและพุทธศาสนาเถรวาท จากหลักฐานตำนานประวัติศาสตร์ นับจากช่วงปลายพุทธศตวรรษที่ 17 เริ่มปรากฏการก่อตั้งรัฐชองชาวลื้อขึ้น ซึ่งตำนานของชาวไทถือพญาเจิงเป็นปฐมกษัตริย์ ชาวไทลื้อ เคลื่อนย้ายมาจากพม่า ส่วนใหญ่ตั้งถิ่นฐานในพื้นที่ภาคเหนือ  ได้แก่ เชียงใหม่ เชียงราย ลำปาง ลำพูน พะเยา แม่ฮ่องสอน  แพร่ น่าน  อัตลักษ์สำคัญคือ เสื้อผ้าการแต่งกาย และภาษา  การเคลื่อนย้ายมาจากหลากหลายเมืองและหลายช่วงเวลาของไทลื้อ ได้ถูกร้อยรัดความเป็นชาติพันธุ์ไว้ด้วยกันภายใต้สมาคมไทลื้อแห่งประเทศไทย โดยมีศูนย์กลางอยู่ที่อำเภอเชียงคำ จังหวัดพะเยา', 'ตระกูลภาษาไท-กะได ภาษาพูด ภาษาไท-ลาว (ใกล้เคียงกับ ไทดำ ไทโยน และลาวล้านช้าง สื่อสารกันเข้าใจ) ภาษาเขียนของชาวไทลื้อสิบสองปันนานั้น ตัวอักขระเป็นแบบเดียวกันกับภาษาของชาวล้านนา อักษร ระบบการสะกดเหมือนกัน แต่อาจมีการออกเสียงต่างกันบ้าง', '100000', 'E36.jpg'),
('E37', 'ไทใหญ่', 'Thaiyai', 'ชาวไทใหญ่ในประเทศไทยนั้นมีทั้งกลุ่มชนดั้งเดิมซึ่งอยู่อาศัยมาเนิ่นนานก่อนการขีดเส้นแบ่งรัฐชาติ ขณะเดียวกันก็มีกลุ่มไทใหญ่ผู้เคลื่อนย้ายหลีกหนีภัยสงครามจากประเทศเมียนมา  รวมถึงกลุ่มแรงงานอีกจำนวนหนึ่ง   กลุ่มคนไทใหญ่ทั้งสามกลุ่ม ต่างมีอัตลักษณ์ร่วมด้านสังคม วัฒนธรรมประเพณี และภาษาที่สามารถสื่อสารกันได้', 'ตระกูลไท-กะได', '100000', 'E37.jpg'),
('E38', 'นามธารี ซิกข์ แขกซิกข์', 'Sikh', 'นามธารี เป็นนิกายหนึ่งของศาสนาซิกข์ ส่วนใหญ่ชาวนามธารีที่อพยพเข้ามาในไทยจะประกอบอาชีพกลุ่มพ่อค้าตั้งถิ่นฐานกระจายในกรุงเทพฯ เชียงใหม่ ลําปาง เชียงราย ศาสถานของชาวนามธารีมี 2 แห่ง คือ บริเวณสี่แยกอโศก สุขุมวิท กรุงเทพฯ และบริเวณถนนช้างม่อยใกล้กับตลาดวโรรส ในจังหวัดเชียงใหม่', 'ภาษาพูด ภาษาปัญจาบีหรืออีกชื่อหนึ่งเรียกว่า คุรุ มุขขิ (Gur Mukhi) ซึ่งแปลว่า ภาษาของครู ภาษาเขียน ภาษาปัญจาบี', '20000', 'E38.jpg'),
('E39', 'บรู', 'Bru', 'วิถีชีวิตของชาวบรูที่บ้านท่าล้งและบ้านเวินบึก สัมพันธ์กับแม่น้ำโขง ดำรงชีวิตร่วมกับวิถีแห่งแม่น้ำทั้งการหาปลา การทำเกษตรริมฝั่ง แม้ชาวบรูจะสร้างบ้านเรือนเป็นหมู่บ้านตามริมแม่น้ำ แต่ก็ขึ้นภูเขาไปทำไร่ในช่วงฤดูเพาะปลูก มีอาณาบริเวณกำหนดเฉพาะรับรู้กันว่าเป็นของกลุ่มตระกูล (จุมรี๊ต) นอกจากการทำเกษตรแล้วชาวบรูบ้านเวินบึกยังเก็บของป่าล่าสัตว์เพื่อยังชีพ', 'ภาษาบรู (Bru) จัดอยู่ในภาษาศาสตร์สาขากระตูอิค (Katuic) หมวดภาษามอญ-เขมร (Mon-Khmer) กลุ่มตระกูลออสโตรเอเชียติก (Astro-Asiatic)', '500', 'E39.jpg'),
('E40', 'บีซู', 'Bi Su', 'ชาวบีซูในประเทศไทยอาศัยอยู่กลุ่มเล็กๆ ใน อำเภอเมืองและอำเภอแม่ลาว จังหวัดเชียงราย ชาวบีซูนับถือศาสนาพุทธและมีความผูกพันแน่นแฟ้นกับวัดและพระสงฆ์ ขณะเดียวกันก็ยังคงนับถือผีด้วยเช่นกัน มีทั้งผีหมู่บ้าน ผีในป่า ผีในถ้ำ ในทุ่งนาและผีบรรพบุรุษ บีซูเรียกผีที่ดูแลหมู่บ้านหรือเสื้อบ้านว่า ‘อังจาว’ ซึ่งชาวบ้านบีซูจะให้ความเคารพนับถืออังจาวเป็นอย่างมากโดยเชื่อว่า เป็นสิ่งศักดิ์สิทธิ์ที่ช่วยคุ้มครองหมู่บ้านของตนให้ปลอดจากอันตรายได้ ดังนั้นคนบีซูจึงมีประเพณีที่สำคัญคือประเพณีไหว้หอเสื้อบ้านที่เรียกกันว่า “อังจาวไว” ', 'ภาษาบีซู จัดอยู่ในภาษาโลโลใต้ สาขาย่อยของตระกูลภาษาทิเบโต-เบอร์มา (Tibeto-Burman)', '500', 'E40.jpg'),
('E41', 'ปกาเกอะญอ จกอ คานยอ กะเหรี่ยง', 'Karen / Kan Yo', 'กะเหรี่ยง เป็นคำที่มักหมายรวมถึงกลุ่มที่เรียกตนเองว่า ปกาเกอะญอ โพล่ง กะยาห์ กะยัน บเวและปะโอ ที่อาศัยอยู่ในภาคเหนือมีการตั้งถิ่นฐานของกะเหรี่ยงปกาเกอะญอบริเวณรอยต่อตะเข็บชายแดนไทย-เมียนมา ตั้งแต่ภาคตะวันตกจนถึงภาคเหนือประเทศไทย ก่อนการแบ่งแนวเขตแดนระหว่างประเทศพม่าหรือสาธารณรัฐเมียนมากับประเทศไทย', 'ตระกูล ทิเบต-พม่า', '590000', 'E41.jpg'),
('E42', 'ปลัง', 'Plang', 'จุดแรกที่ชาวปลังจะอพยพเข้ามาคือ อ.แม่จัน, อ.แม่สาย, บ้านห้วยน้ำขุ่น อ.แม่สรวย, อ.เวียงป่าเป้า, อ.แม่ฟ้าหลวง, อ.เมือง จ.เชียงราย  หลังจากนั้น ชาวปลังจะเดินทางไปยังจังหวัดต่างๆ ศาสนาดั้งเดิมของชาวปลังคือศาสนาผีและพุทธ ต่อมาราว 100 กว่าปีก่อน ชาวปลังได้เริ่มมานับถือศาสนาคริสต์นิกายแบพติสต์ ด้วยเหตุผลที่ศาสนาเดิมมีค่าใช้จ่ายในการเลี้ยงผีจำนวนมากจนฐานะทางเศรษฐกิจที่มีไม่สามารถรองรับกับระบบความเชื่อได้ จึงนำไปสู่การนับถือศาสนาคริสต์ ', 'ภาษาปลังจัดอยู่ในตระกูลภาษาออสโตรเอเชียติก (Austro-Asiatic) กลุ่มเหนือ สาขาย่อยปะหล่องตะวันออก (East-Palaungic) ในกลุ่มว้า (Waic) สาขาย่อยบูหลัง/ปูหลัง (Bulang)', '1200', 'E42.jpg'),
('E43', 'ปะโอ', 'Pa O', 'ชาวปะโออาศัยอยู่บริเวณรัฐฉานและที่ราบริมฝั่งแม่น้ำคง (สาละวิน) รวมกับชาวไทใหญ่ รวมไปถึงบางส่วนของรัฐมอญและรัฐกระเหรี่ยง และเนื่องด้วยชาวปะโอตั้งถิ่นฐานเป็นเพื่อนบ้านกับชาวไทใหญ่จึงมีการติดต่อไปมาหาสู่จนมีวัฒนธรรมที่คล้ายคลึงกัน', 'ภาษาปะโอ จัดอยู่ในตระกูลภาษาจีน-ทิเบต ตระกูลภาษาย่อยทิเบต-พม่า สาขากะเหรี่ยง', '300', 'E43.jpg'),
('E44', 'เปอรานากัน ', 'Per Ra Na Gun', 'กลุ่มวัฒนธรรมจีนเชื้อสายมาลายู หรือเปอรานากัน หรือบ้าบ๋า-ย่าหยา กระจัดกระจายอยู่ทั่วไปในแทบหมู่เกาะในแถบมาเลเซีย อินโดนีเซีย และไทย โดยในประเทศไทยนั้นพบอยู่มากในแถบจังหวัดชายทะเลฝั่งอันดามัน เช่น ภูเก็ต พังงา กระบี่  มีอัตลักษณ์ด้านอาหารและการแต่งกายที่ผสมผสานกันระหว่างความเป็นจีนและความเป็นมาลายู', 'ภาษาพูดใช้ภาษามลายูบ้าบ๋า เป็นภาษาถิ่นหนึ่งของภาษามลายู ชาวเปอรานากันในไทยใช้ภาษาไทยถิ่นใต้ ที่เจือไปด้วยคำศัพท์จากภาษามลายู จีน และอังกฤษ ไม่มีภาษาเขียนของตนเอง ใช้ภาษาของรัฐชาติที่ชาวเปอนารากันอาศัยอยู่', '5000', 'E44.jpg'),
('E45', 'ผู้ไท ภูไท', 'Pu Thai', 'ชาวภูไทอาศัยอยู่ในจังหวัดต่าง ๆ ทางภาคตะวันออกเฉียงเหนือของประเทศไทย ได้แก่ จังหวัดกาฬสินธุ์ นครพนม มุกดาหาร จังสกลนคร และอุบลราชธานีและจังหวัดอุดรธานีบางส่วน โดยกลุ่มต่างๆ มีลักษณะร่วมกันในด้านประวัติความเป็นมา ความเชื่อ ประเพณี พิธีกรรม วิถีความเป็นอยู่ และศิลปวัฒนธรรม', 'ภาษาภูไทเป็นภาษาไทภาษาหนึ่งและเหมือนกับภาษาลาว เพราะชาวภูไทไม่มีอักษรของตนเอง ต้องยืมตัวอักษรของชาวลาว ภาษาภูไทจึงกลมกลืนกับภาษาลาว การเขียนตัวอักษรชาวภูไทจึงมีการประยุกต์วิธีการเขียนของชาวลาว', '470000', 'E45.jpg'),
('E46', 'พวน ไทพวน', 'Thaipuan', 'ชาวพวน  เคลื่อนย้ายมายังประเทศไทยและตั้งชุมชนมาอย่างยาวนาน  กระจัดกระจายอยู่ในพื้นที่ภาคกลาง ภาคเหนือตอนล่าง และภาคอีสาน โดบเฉพาะชาวพวนที่บ้านทราย อำเภอบ้านหมี่ จังหวีดลพบุรี  ถือเป็นชุมชนชาวพวนที่คงอัตลักษณ์ไว้ได้อย่างเหนียวแน่น ทั้งด้านอาหาร บ้านเรือน การแต่งกาย ประเพณีวัฒนธรรม  โดยมีการจัดสร้างพิพิธภัณฑ์เพื่อบอกเล่าเรื่องราวของตนเองไว้ที่วัดบ้านทราย', 'ภาษาไท-กะได (ตระกูล South-western Tai)', '200000', 'E46.jpg'),
('E47', 'โพล่ง', 'Phong', 'คนไทยในภาคเหนือเรียกกะเหรี่ยงโพล่ง ว่า \"พล่อ\" หรือ \"โพล่ง\" มีประชากรเป็นอันดับสองรองจากกะเหรี่ยงปกาเกอะญอ ผู้คนส่วนใหญ่มักเข้าใจว่าปกาเกอะญออาศัยอยู่เฉพาะทางภาคเหนือและกะเหรี่ยงโผล่งอาศัยอยู่เฉพาะทางภาคตะวันตกเท่านั้น แต่แท้จริงแล้วพบกะเหรี่ยงทั้งสองกลุ่มอยู่ในจังหวัดเดียวกันแต่แยกหมู่บ้านไม่ปะปนกัน กะเหรี่ยงโผล่ว มีทั้งส่วนที่นับถือศาสนาคริสต์ นับถือผีบรรพบุรุษและส่วนมากนับถือศาสนาพุทธ ', 'ตระกูลภาษาจีน-ธิเบต', '100000', 'E47.jpg'),
('E48', 'ม้ง', 'Mong', 'ม้ง  เป็นกลุ่มชาติพันธุ์ที่มีจำนวนมากและกระจายตัวอยู่กว้างขวางในบริเวณภาคเหนือของประเทศไทย  มีอัตลักษณ์การแต่งกายที่โดดเด่น กับทั้งภาพลักษณ์ด้านความขยันหมั้นเพียร อดออม และค้าขายเก่ง  มนประเทศไทยมีชาวม้งสองกลุ่มหลักคือ ม้งเขียว และม้งขาว  ', 'ภาษาม้ง จัดอยู่ในกลุ่มตระกูลภาษาม้ง-เมี่ยน', '150000', 'E48.jpg'),
('E49', 'มลาบรี', 'Mlabri', 'ชาวมลาบรี แต่เดิมนั้นเป็นกลุ่มชนที่ดำรงชีพแบบหาของป่าล่าสัตว์ เคลื่อนย้ายไปตามแหล่งทรัพยากร หากแต่เมื่อมีโครงการพัฒนาคุณภาพชีวิตหลายโครงการเข้าไปสนับสนุนการดำรงชีพ ชาวมลาบรีในพื้นที่จังหวัดแพร่และจังหวัดน่านเริ่มมีพื้นที่ทำมาหากินและสร้างชุมชนถาวรเพิ่มมากขึ้น หากแต่ยังคงใช้ฐานทรัพยากรธรรมชาติและพื้นที่ป่าในการก็บหาของป่าทั้งเป็นอาหารและสร้างรายได้ อย่างไรก็ตามชีวิตที่เคลื่อนย้ายจึงยังอาจไม่ได้หมดความสำคัญต่อชาวมลาบรีลงทั้งหมด ยังคงมีการเคลื่อนย้ายไป-มาในระหว่างพื้นที่ อาจกล่าวได้ว่าการเคลื่อนย้ายเกิดขึ้นในทิศทางและพื้นที่เฉพาะเจาะจงมากยิ่งขึ้น โดยไม่เพียงแต่จำกัดในดินแดนรัฐชาติไทยเท่านั้น หากยังมีการจำกัดเหลือพื้นที่ไม่กี่แห่งอีกด้วย', 'ภาษามลาบรีเป็นภาษาในตระกูลออสโตรเอเชียติค สาขามอญ-เขมร กลุ่มย่อยขมุอิค', '200', 'E49.jpg'),
('E50', 'มลายูมุสลิม', 'Malay Muslim', 'มลายูมุสลิมในประเทศไทยส่วนใหญ่อยู่ในพื้นที่จังหวัดภาคใต้ มีวิถีชีวิตตามทรัพยากรธรรมชาติ ทั้งการทำประมงชายฝั่งในชุมชนริมทะเล การทำสวนยางพารา นาข้าว สวนผลไม้ ตามบริบทของพื้นที่ซึ่งมีหลากหลายแบบตามลักษณะนิเวศน์ ดำรงชีพอย่างเรียบง่าย มีความเชื่อตามหลักการทางศาสนาผสมผสานความเชื่อท้องถิ่น เมื่อกระแสอิสลามาภิวัตน์แผ่เข้ามาส่งผลต่อวัฒนธรรมของคนมลายูมุสลิมบางส่วนที่ผสมผสานกับประเพณีดั้งเดิมของท้องถิ่น ประเพณีและศิลปะการแสดงของคนมลายูหลายอย่างเริ่มเลือนหายไป อัตลักษณ์ของคนมลายูที่เคยผสมผสานวัฒนธรรมของท้องถิ่นเปลี่ยนแปลงไปยึดโยงกับศาสนาอิสลามอย่างเข้มข้นขึ้น', 'ภาษามลายู หรือเรียกอีกอย่างว่าภาษามาเลย์ (Bahasa Melayu บาฮาซา มลายู) เป็นภาษาตระกูลออสโตรนีเซียน', '1613000', 'E50.jpg'),
('E51', 'มอแกน', ' Mogan', 'ชาวมอแกน ใช้ชีวิตสัมพันธ์เป็นหนึ่งเดียวกับท้องทะเล อดีตใช้เรือก่าบาง เรือฉ่าพัน สัญจรไปมาและเป็นที่พักอาศัยก่อนการลงหลักปักฐานสร้างชุมชนริมทะเล เกาะแก่งที่กระจายอยู่ในทะเลฝั่งอันดามัน  ชุมชนชาวมอแกนในประเทศไทยมี 5 แห่ง และมีมอแกนกระจายตัวไปอยู่ตามชุมชนชาวเลอื่นๆ แต่ไม่ได้อยู่เป็นกลุ่มใหญ่เหมือน 5 ชุมชนที่เกาะเหลา เกาะพยามและเกาะช้าง จังหวัดระนอง หมู่เกาะสุรินทร์ จังหวัดพังงาและหาดราไวย์ จังหวัดภูเก็ต ชาวมอแกนอาศัยท้องทะเลเป็นแหล่งอาหาร จับหาสัตว์น้ำด้วยเครื่องมือประมงขั้นพื้นฐาน ใช้ทรัพยากรรรมชาติในการดำรงชีพ', 'ภาษาพูดใช้ ภาษามอแกน อยู่ตระกูลภาษาออสโตรนีเชียน ไม่มีภาษาเขียน', '1000', 'E51.jpg'),
('E52', 'มอแกลน', 'Moglan', 'ชาวมอแกลนตั้งถิ่นฐานอยู่ในประเทศไทยมายาวนานตั้งแต่สมัยบรรพบุรุษ  หมู่บ้านของชาวมอแกลนอยู่ชายฝั่งทะเลหรือด้านในแผ่นดิน  หลายชุมชนมีตำนานเรื่องวีรบุรุษที่เป็นต้นกำเนิดของชาวมอแกลน ชื่อว่า “พ่อตาสามพัน”', 'ไม่มีภาษาเขียน ใช้ภาษามอแกลนเป็นภาษาพูด อยู่ในตระกูลภาษาออสโตรนีเซียน', '4500', 'E52.jpg'),
('E53', 'มอญ', 'Mon', 'ชาวมอญ ถือเป็นกลุ่มชาติพันธุ์ที่เคร่งครัดในพระพุทธศาสนา ขณะเดียวกันก็เชื่อมั่นในความเชื่อดั้งเดิม  เราจะเห็นวิถีของชาวมอญที่ผสมผสานกันระหว่างพุทธและผี ผ่านการดำรงชีวิต ประเพณีและพิธีกรรมในรอบปี  ', 'ภาษามอญอยู่ในสายโมนิก (Monic branch) ของตระกูลมอญ-เขมร ซึ่งอยู่ในตระกูลภาษาออสโตรเอเชียติก (Austroasiatic phylum)', '400000', 'E53.jpg'),
('E54', 'มานิ', 'Ma Ni', 'เขตป่าในเทือกเขาบรรทัด พื้นที่จังหวัดตรัง สตูล สงขลาและพัทลุง พบว่า มานิมีการดำรงชีวิตที่แตกต่างกันหลายลักษณะ   แต่ละกลุ่ม/พื้นที่ จะมีปัจจัยต่อการดำรงชีวิตและการเผชิญกับความเปลี่ยนแปลงไปจากวิถีชีวิตแบบดั้งเดิมในลักษณะต่างกัน ดังนั้นจึงสามารถแบ่งลักษณะทางสังคมของมานิเป็น 3 ลักษณะ ดังนี้ กลุ่มเร่ร่อนอพยพหาของป่า-ล่าสัตว์แบบดั้งเดิม กลุ่มกึ่งเร่ร่อน-เริ่มตั้งถิ่นฐานและกลุ่มที่ตั้งถิ่นฐานถาวรแล้ว', 'ภาษามานิ จัดอยู่ในกลุ่มภาษาอัสเลียน สาขาภาษามอญ-เขมรใต้ ตระกูลภาษาออสโตรเอเชียติก แต่ไม่มีภาษาเขียน', '100', 'E54.jpg'),
('E55', 'ยะไข่', 'Ya Kai', 'ชาวยะไข่มีลักษณะการย้ายถิ่นฐานที่ค่อนข้างเป็นรูปแบบตายตัว โดยมักจะเริ่มเคลื่อนย้ายมาจากขอบเขตชายแดนทางฝั่งพม่าที่ติดกับดินแดนประเทศไทย อาทิเช่น บริเวณอำเภอแม่สอด จังหวัดตาก ถือเป็นแหล่งอนุบาลแรงงานชาวพม่าที่สำคัญ เพื่อฝึกปรือความสามารถทั้งทางทักษะแรงงานและทางภาษา โดยส่วนใหญ่เมื่อแรงงานชาวพม่าสามารถพูดภาษาไทยได้จะเริ่มขยับขยายถิ่นฐานเข้าสู่การเป็นแรงงานในเมืองหลวง เช่น กรุงเทพมหานครและบริเวณพื้นที่ใกล้เคียง', 'ตระกูลภาษาจีน- ธิเบต กลุ่มภาษาแม้ว – เย้า', '100000', 'E55.jpg'),
('E56', 'ยุมบรี', 'Yumbury', 'ในอดีตคนไทยรู้จักกลุ่มชาติพันธุ์ \"มลาบรี\" ในชื่ออื่นที่แสดงถึงอคติทางวัฒนธรรม เช่น ผีป่า ผีตองเหลือง คำว่า \"มลาบรี\" แท้จริงแล้วมีความหมายว่า \"คนป่า\" อาศัยอยู่ในจังหวัดแพร่และน่าน ปัจจุบันทรัพยากรธรรมชาติที่เป็นรากฐานทางวัฒนธรรมของกลุ่มชาติพันธุ์ได้ถูกทำลายลงอย่างมากจำต้องเปลี่ยนวิถีชีวิตไปจากเดิม', 'ภาษามลาบรีเป็นภาษาในตระกูลออสโตรเอเชียติค สาขามอญ-เขมร กลุ่มย่อยขมุอิค', '1900', 'E56.jpg'),
('E57', 'ลัวะ', 'La Wa', 'ลัวะ ในประเทศไทยนั้น หากจำแนกตามลักษณะการตั้งถิ่นฐานแล้วจะพบว่ามีีสองกลุ่มใหญ่ คือ หนึ่ง ลัวะในเมืองเชียงใหม่ แม่ฮ่องสอน ที่เรียกตัวเองว่าลเวือะ ลวะ  สืบผีฝ่ายพ่อ  และสอง ลัวะ ในเมืองน่าน ที่เรียกตัวเองว่า ลัวะมัล ลัวะปรัย สืบผีฝ่ายแม่  ทั้งสองกลุ่มใช้ภาษาพูดที่แตกต่างกัน ไม่สามารถสื่อสารกันได้เข้าใจ เลอเวอะที่บ้านป่าแป๋ อ.แม่สะเรียง จ.แม่ฮ่องสอน ยังคงดำรงชีพโดยพึ่งพิงระบบเกษตร ทั้งทำข้าวไร่ ข้าวนา หาพืชอาหารจากป่าธรรมชาติรายรอบชุมชน บ้านเรือนมีความมั่นคงมากขึ้นจากเดิมใช้ไม้ไผ่เปลี่ยนมาใช้ไม้ในการสร้างบ้าน ผู้หญิงในชุมชนมีอาชีพเสริมคือการทอผ้า ถือเป็นชุมชนที่ยังรักษาประเพณีดั้งเดิมไว้ได้อย่างเหนียวแน่น ยังมีสะมังทำหน้าที่ในการปกครองชุมชนและเป็นผู้นำในการประกอบพิธีกรรม', 'ตระกูลออสโตรเอเชียติก (Austro-Asiatic) ตระกูลย่อย มอญ-เขมร', '10000', 'E57.jpg'),
('E58', 'ลาวครั่ง', 'Lao Kung', 'ลาวครั่ง มีอัตลักษณ์สำคัญคือการทอผ้า โดยเฉพาะการย้อมเส้นด้ายให้เกิดสีแดงด้วยครั่ง การมอผ้าและการทอตีนจกด้วยลวดลายอันวิจิตร  ถือเป็นองค์ความรู้เฉพาะของชาวลาวครั่ง ถึงแม้ปัจจุบันจะหาครั่งเพื่อย้อมผ้าไม่ได้แล้ว  จำเป็นต้องใช้สีเคมีสังเคราะห์ทดแทน แต่ชาวลาวครั่งยังนิยมย้อมผ้า หรือสวมใส้ผ้าถุงที่มีสีแดงครั่งตามอัตลักษณ์ของตนเอง', 'ตระกูลภาษาไท-กะได ภาษาลาวครั่งไม่มีอักษรและไม่มีระบบการเขียนเป็นการเฉพาะ ใช้อักษรลาวร่วมกันกับกลุ่มชาติพันธุ์ลาวอื่น ๆ', '90000', 'E58.jpg'),
('E59', 'ลาวแง้ว', 'Lao Ngae', 'ปัจจุบันกลุ่มผู้เรียกตนเองว่า ลาวแง้ว ตั้งเป็นชุมชนกระจายอยู่แถบสระบุรี ลพบุรี สิงห์บุรีและนครสวรรค์ เรื่องเล่าการเดินทางที่ตกทอดสู่ลูกหลาน บ้างว่ามาจากเมืองหลวงพระบาง แต่ในขณะบางคนเล่าว่ามาจากเวียงจันทน์ในครั้งศึกเจ้าอนุวงศ์ ความทรงจำจึงค่อนข้างสับสนว่าตนเป็นลาวกลุ่มใดและถูกต้อนมาครั้งใด ชาวลาวแง้ว กระจายตัวอยู่ในพื้นที่ภาคกลาง บริเวณจังหวัดสิงบุรี ลพบุรี เป็นชุมชนดั้งเดิมก่อร่างสร้างชุมชนมาเนื่นนาน ส่วนใหญ่ทำกสิกรรม ทั้งปลูกข้าว  ทำไร่ ทำสวน  รวมถึงการแปรรูปผลผลิต ในด้านประเพณีนั้นยังคงสืบทอดวัฒนธรรมดั้งเดิม อาจจะมีการปรับปรนให้สอดคล้องกับยุคสมัยบ้างในบางประเพณี ', 'ตระกูลภาษาไท-กะได ', '30000', 'E59.jpg'),
('E60', 'ลาวตี้', 'Lao Ty', 'ลาวตี้ ชื่อเรียกกลุ่มคนที่มีเชื้อสายลาวอพยพจากเมืองเวียงจันทน์ ตั้งถิ่นฐานสมัยกรุงธนบุรีและกรุงรัตนโกสินทร์ตอนต้น ถูกกำหนดให้อยู่เป็นถิ่นที่ เนื่องจากเหตุผลด้านเศรษฐกิจและการปกครอง อย่างไรก็ตามการย้ายถิ่นของชาวลาวตี้เริ่มปรากฏมากขึ้นหลังมีการยกเลิกระบบไพร่ ปัจจุบันพบการตั้งถิ่นฐานในหลายพื้นที่', 'ตระกูลภาษา ไต-กระได (Tai-Kadai)', '10000', 'E60.jpeg'),
('E61', 'ลาวเวียง', 'Lao Waing', 'ลาวเวียง กลุ่มชาติพันธุ์ที่โยกย้ายถิ่นฐานจากเมืองเวียงจันทน์ เข้าสู่ประเทศไทยตั้งแต่สมัยกรุงธนบุรี ตั้งชุมชนกระจายอยู่ในภาคเหนือตอนล่างและภาคกลาง การเริ่มสร้างชุมชนใหม่บนแผ่นดินที่ไม่คุ้นเคย ปรับตัวรวมกลุ่ม สร้างเครือข่าย สร้างสำนึกทางชาติพันธุ์ ทั้งยังสามารถธำรงประเพณีวัฒนธรรมไว้ได้อย่างน่าสนใจ', 'ตระกูลภาษา ไท-กะได ภาษาพูด ภาษาลาวเวียง', '200000', 'E61.jpg'),
('E62', 'ลาวอีสาน', 'Lao Isan', '\"กลุ่มชาติพันธุ์ลาวอีสาน\" หรือกลุ่มประชากรที่ใช้ภาษาอีสานอยู่ในชีวิตประจำวันซึ่งอาศัยอยู่ภาคตะวันออกเฉียงเหนือ พื้นที่ที่มีความหมายตรงกับ \"คำอีสาน\" เริ่มใช้เป็นทางการสมัยรัชการที่ 5 ราว พ.ศ. 2442 ในชื่อ มณฑลตะวันออกเฉียงเหนือ แต่ยังหมายเฉพาะลุ่มน้ำมูลถึงอุบลราชธานี จำปาสัก ฯลฯ รวมความแล้วบุคคลใดก็ตามที่มีถิ่นกำเนิดหรือมีหลักแหล่งถิ่นฐานอยู่ทางภาคตะวันออกเฉียงเหนือของไทย ถ้าถือตัวว่าเป็นคนอีสานหรือชาวอีสานอย่างเต็มอกเต็มใจและอย่างองอาจก็ถือเป็นคนอีสานเป็นชาวอีสานทั้งนั้น', 'ตระกูลภาษา ไท-กะได', '14 ล้าน', 'E62.jpg'),
('E63', 'ลาหู่ ลาฮู', 'La Hoo', '\"ข้าวปุก\" วิถีการผลิตและการกินที่มีความเกี่ยวข้องกับพิธีกรรมและความเชื่อ สัมพันธ์ทางสังคม จริยธรรมและสุขภาพ ของชาวลาหู่ผู้มีภูมิลำเนาเดิมอยู่บริเวณรอยต่อระหว่างธิเบตและจีน ถูกรุกรานจากชาวจีนหลายครั้งจึงอพยพลงใต้ กระทั่งบางกลุ่มเข้ามาอาศัยในประเทศลาว รัฐฉานและไทย ครอบคลุมหมู่บ้านในจังหวัดทางภาคเหนือ', 'ภาษาลาหู่อยู่ในกลุ่มภาษาตระกูลจีน-ทิเบต (Sino-Tibetan) สาขาทิเบต-พม่า กลุ่มภาษาโลโลดั้งเดิม (Proto-Lolo) และแตกสาขาย่อยลงมาในกลุ่มพม่า-โลโล สาขาโลโลกลาง (Central Lolo) ซึ่งอยู่ในกลุ่มเดียวกับภาษาลีซู และภาษาอาข่า', '8000', 'E63.jpg'),
('E64', 'ลีซู', 'Lee Su', 'นักวิชาการสันนิษฐานว่าถิ่นเดิมของลีซูนั้น น่าจะมีจุดเริ่มต้นที่เทือกเขาหิมาลัยทางทิศตะวันออกของธิเบต และกระจายออกไปทางทิศตะวันออกของชายแดนยูนนาน-เสฉวน  อพยพไปจนถึงเมืองติงโฉ่ว ทางตอนเหนือของคุนหมิง และไกลออกไปจนถึงเมืองมิตจีนาของรัฐคะฉิ่น ประเทศพม่า และอพยพลงมาสู่ทางใต้เข้าสู่ทางตอนเหนือของประเทศไทย', 'ภาษาพูด จีนลีซู ภาษาเขียน : ภาษาเขียนฟราเซอร์ ลีซูจัดอยู่ในกลุ่มธิเบต-พม่า (Tibeto-Burman)', '15000', 'E64.jpg'),
('E65', 'เลอเวือะ', 'Loe Wuea', 'คนภายนอกมักเรียก “ชาวเลอเวือะ” ที่บ้านป่าแป๋ว่า “ลัวะ” แต่ก็มีนักวิชาการบางคนเรียกเลอเวือะบ้านป่าแป๋ว่า “ละว้า” และที่อื่นๆ ในประเทศไทยถูกเรียกชื่อว่า \"ละว้า\" ในวงกว้าง ', 'ภาษาพูด จีนลีซู ภาษาเขียน : ภาษาเขียนฟราเซอร์ ลีซูจัดอยู่ในกลุ่มธิเบต-พม่า (Tibeto-Burman)', '5000', 'E65.jpg'),
('E66', 'เวียต', 'Viet', 'ชาวเวียดนามอพยพในเขตอำเภอท่าบ่อ จังหวัดหนองคาย เป็นกลุ่มชาวเวียดนามที่อพยพลี้ภัยสงครามมา เมื่อได้รับสัญชาติไทยซึ่งเราเรียกว่าคนไทยเชื้อสายเวียดนาม และเขตเทศบาลตำบลท่าบ่อ อำเภอท่าบ่อ จังหวัดหนองคาย เป็นบริเวณที่ชาวไทยเชื้อสายเวียดนามตั้งถิ่นฐานอยู่อาศัยมากที่สุดแห่งหนึ่งของเมืองไทย ชาวเวียดนามที่อพยพโยกย้ายมายังประเทศไทยนั้นได้นำเอาวัฒนธรรมในด้านต่างๆ ของตนเองเข้ามา โดยเฉพาะกับมิติความเชื่อ กล่าวคือชาวเวียดนามนอกจากจะนับถือศาสนาแล้วยังมีการนับถือผีควบคู่ไปด้วย โดยมีทั้งผีบ้านและเจ้าที่เป็นที่เคารพของทุกคนซึ่งผีบ้านของชาวเวียดนามจะเป็นผีบรรพบุรุษในครอบครัวหรือสายสกุลของตน ', 'ตระกูลออสโตรเอเชียติก', '20000', 'E66.jpg'),
('E67', 'สะกอ ', 'Sa Go', 'เป็นหนึ่งในกลุ่มของชาติพันธุ์กะเหรี่ยงที่อาศัยอยู่ทางภาคเหนือของประเทศไทย ชาวกะเหรี่ยงเชียงใหม่จำแนกออกตามลักษณะเอกลักษณ์ ภาษา และวัฒนธรรมการแต่งกายได้เป็น 2 กลุ่มหลักคือ กะเหรี่ยงสะกอกับกะเหรี่ยงโพล่ง ความแตกต่างของสองกลุ่มนี้จะอยู่ที่เสื้อผ้าการแต่งตัวของหญิงแม่บ้าน และสำเนียงภาษา', 'ตระกูลภาษา ทิเบต-พม่า (Tibeto-Burman) ภาษาพูดภาษากะเหรี่ยง ภาษาเขียนอักษรลิวา', '5000', 'E67.jpg'),
('E68', 'แสก', 'Sak', 'กลุ่มชาติพันธุ์แสกจัดอยู่ในตระกูลพูดภาษาไท-กะได อาศัยอยู่กระจัดกระจายหนาแน่นในพื้นที่จังหวัดนครพนม ไทแสกมีภาษาพูดของกลุ่มชาติพันธุ์ตัวเอง แต่ไม่มีภาษาเขียน โดยจะใช้ภาษาแสกในการพูดจาสื่อสารกันภายใน แต่จะใช้ภาษาลาวอีสานหรือภาษาไทยกลางกับกลุ่มชาติพันธุ์อื่นแทน', 'ตระกูลพูดภาษาไท-กะได (Tai-Kadai Language Family) ภาษาพูด ภาษาแสก ภาษาลาว/ไทอีสาน ภาษาไทย ภาษาเขียน ภาษาไทย', '3500', 'E68.jpg'),
('E69', 'โส้', 'So', 'โส้ เป็นกลุ่มชาติพันธุ์หนึ่ง ในภาคตะวันออกเฉียง เหนือ ซึ่งตั้งหลักแหล่งกระจายตัวอยู่ในจังหวัดต่าง ๆ ได้แก่ สกลนคร นครพนม มุกดาหาร โดยในจังหวัดสกลนคร มีชาวโส้อาศัยอยู่เป็นจำนวนมาก เนื่องจากเป็นจุดเริ่มต้นการตั้งถินฐานของช้าวโส้ที่เคลื่อนย้ายมาจากแม่ข้ำโขงฝั่งขวา โดยเฉพาะที่ชุมชนโพธิไพศาลนั้น ถือว่าเป็นชุมชนใหญ่ที่ชาวโส้อาศัยอยู่ โดยกองการศึกษา เทศบาลตำบลกุสุมาลย์ ศูนย์วัฒนธรรมเฉลิมราช ศูนย์วัฒนธรรมไทโส้ จากตำนานการอพยพของชาวโส้ที่อำเภอกุสุมาลย์ ซึ่งมีความเกี่ยวข้องกับชาวโส้บ้านโพธิไพศาล ได้กล่าวถึงสาเหตุของเมืองเก่าร้างว่าเกิดมาจากสาเหตุ 2 ประการ ได้แก่ ความแตกแยก และความอดอยากแร้นแค้น ', 'ตระกูลออสโตรเอเซียติก สาขาย่อยมอญ-เขมร อยู่ในกลุ่มมอญ-เขมรตะวันออก และกลุ่มย่อยกะตู ภาษาโส้มีเฉพาะภาษาพูด ไม่มีภาษาเขียน หากแต่ใช้สัทอักษรสากลและสัทอักษรไทยแทนเสียงพูด', '70000', 'E69.jpg'),
('E70', 'อ่าข่า', 'A Kha', 'อ่าข่าในประเทศไทย อาศัยกระจายตัวอยู่ใน 7 จังหวัดทางภาคเหนือ ประกอบด้วยกลุ่ม อู่โล้, ลอมี้, อู่เบียะ, อ๊ะจ๊อ, หน่าคะ, อู่พี และอะเคอ ชาวอ่าข่ากลุ่มแรกเข้ามาอาศัยอยู่บริเวณดอยตุงและบ้านดอยแสนใจ อ.แม่ฟ้าหลวง จ.เชียงราย ', 'ตระกูลภาษาทิเบโต-เบอร์มัน (Tibeto-Burman)', '50000', 'E70.jpg'),
('E71', 'อาเค๊อะ', 'A Khea', 'ชาวอาเคอะที่อาศัยอยู่ในแถบอำเภอแม่สรวย จังหวัดเชียงรายยังคงดำรงชีพในวิถีเกษตรพึ่งพิงทรัพยากร ธรรมชาติ รักษาความเชื่อดั้งเดิมและยังคงธำรงอัตลักษณทางชาติพันธุ์ผ่านประเพณีวัฒนธรรมที่ยังคงสืบทอดกันจากรุ่นสู่รุ่น โดยมีพิธีเซ่นไหว้ประตูหมู่บ้าน เรียกว่า “มีกอลอแนะ” เป็นพิธีที่คนทั้งชุมชนร่วมกันเซ่นสรวงผีดี ทั้งผีเจ้าป่าและผีบรรพบุรุษให้มาช่วยปกป้องคุ้มครองให้คนในหมู่บ้านอยู่อย่างสงบสุข', 'ตระกูลภาษาทิเบโต-เบอร์มัน (Tibeto-Burman) สาย Southern Ngwi', '400', 'E71.jpg'),
('E72', 'อิ้วเมี่ยน', 'Eiw Mien', 'ชาวอิ้วเมี่ยน  เป็นกลุ่มชาติพันธุ์เชื้อชาติมองโกลอยด์ที่มีอายุเก่าแก่ใกล้เคียงกับชาวฮั่น โดยมีถิ่นฐานกำเนิดบริเวณเขตภูเขาสูงทางตอนกลางของประเทศจีน และดำรงชีพด้วยการเกษตรแบบตัดฟันโค่นเผา สำหรับชาวอิ้วเมี่ยนกลุ่มที่อาศัยอยู่ทางภาคเหนือตอนบนของประเทศไทย เป็นกลุ่มที่พูดภาษาเมี่ยนและเรียกตัวเองว่า  “เมี่ยน” หรือ “อิ้วเมี่ยน” โดยอพยพเข้ามาในประเทศไทยเมื่อ 200 ปีก่อน  การปรับตัวเพื่อความอยู่รอดทางเศรษฐกิจก็ส่งผลทำให้ชาวอิ้วเมี่ยนคลายความยึดถืออัตลักษณ์ทางวัฒนธรรมของตนเอง จึงเป็นอีกกลุ่มชาติพันธุ์หนึ่งในประเทศไทยที่อาจมีความเสี่ยงที่จะสูญเสียอัตลักษณ์ทางชาติพันธุ์บางส่วนของตน', 'ตระกูลภาษาจีน- ธิเบต กลุ่มภาษาแม้ว – เย้า', '49000', 'E72.jpg'),
('E73', 'อูรักลาโวยจ', 'U Rak La Woij', 'วิถีชีวิตชาวเลในอดีต ใช้เรือเป็นที่อยู่อาศัย ยานพาหนะ สำหรับเดินทางเป็นเครื่องมือทำมาหากินและจะย้ายขึ้นฝั่งในช่วงฤดูฝนหรือมีพายุคลื่นลมแรงหรือต้องการน้ำจืด ปัจจุบันชาวอูรักลาโวยจ สร้างบ้านเรือนถาวร และใช้ฐานทรัพยากรเพื่อการดำรงชีพ ทั้งจับหาสัตว์น้ำ เก็บหาพืชอาหารจากป่าชายเลน ป่าสันทราย ชาวอูรักลาโวยจให้ความสำคัญกับพิธีลอยเรือเพื่อที่จะสะเดาะเคราะห์ ส่งวิญญาณบรรพบุรุษกลับไป “ฆูนุงฌึรัย” ซึ่งเชื่อว่าเป็นบ้านเมืองเดิมของบรรพบุรุษและส่งสัตว์ที่ฆ่ากินเป็นอาหารกลับไปให้เจ้าของเดิมเพื่อไถ่บาป ', 'ไม่มีภาษาเขียน ใช้ภาษาอูรักลาโวยจเป็นภาษาพูด อยู่ในตระกูลภาษาออสโตรนีเซียน กลุ่มภาษามาลาโย-โพลิเนเซียน สาขามาเลย์อิก สาขาย่อยมาลายัน', '3000', 'E73.jpg'),
('E74', 'โอก๋อง', 'O-Gong', 'ประวัติความเป็นมาของชาวโอก๋องยังไม่ทราบที่มาแน่ชัด เดิมชาวโอก๋องคงอยู่ใกล้กับเขตแดนไทย-พม่า และอาจเข้ามาพร้อมกับสงครามไทยกับพม่าช่วงสมัยใดสมัยหนึ่ง ตามที่มีการสันนิษฐานว่า ชาวโอก๋องคงเข้ามาพร้อมกับการกวาดต้อนของกองทัพไทยราว 200 ปีมาแล้ว โดยอยู่ที่กาญจนบุรี แล้วจึงค่อยๆ อพยพมาที่สุพรรณบุรีและอุทัยธานี', 'ภาษาพูดจัดอยู่ในตระกูลภาษาจีน-ทิเบต (Sino-Tibeton) สาขาภาษาโลโล-พม่า (Lolo-Burmese) สาขาย่อยก๊อง ไม่มีภาษาเขียน', '600', 'E74.jpg');
